import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,
    IonLabel, IonList, IonRadio, IonRadioGroup, IonInput, IonCheckbox
} from '@ionic/react';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { format, subMinutes } from 'date-fns'
import { Route, useParams } from "react-router";
import baseUrl, { api } from '../../Urls';
const QuizQuestion: React.FC<{
    courseId: string,

}> = (props) => {
    type QuizQuestion = {
        id: string;
    };
    const { id } = useParams<QuizQuestion>();
    const history = useHistory();
    const [question, setQuestion] = useState([]);
    const [isAns, setIsAns]: any[] = useState([]);
    const [test_end, setTest_end] = useState(format(new Date(), 'HH:mm:ss'));
    const [quizId, setQuizId] = useState('');
    const [time, setTime] = React.useState(format(new Date(), 'HH:mm:ss'));
    const [modalOpen, setModalOpen] = useState(false);
    useEffect(() => {
        const timerID = setInterval(() => {
            tick()
            if (time === format(new Date(test_end), 'HH:mm:ss')) {
                submit()
            }

        }, 1000);
        return function cleanup() {
            clearInterval(timerID);
        };
    });

    function tick() {
        setTime(format(new Date(), 'HH:mm:ss'));

    }

    useEffect(() => {
        return history.listen(() => { // listen
            if (history.action === "POP") {
                history.replace("/dashboard/mycourse/4/quiz/127");
            }
        });
    }, [history]);
    //////
    useEffect(() => {
        fetch(`${baseUrl}${api.viewQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setQuestion(result.question)
                setTest_end(result.question[0].test_end)
                setQuizId(result.question.map((item: any) => (item.quiz_id)))
                localStorage.setItem('quiz_id', result.question[0].quiz_id)
            })

    }, []);


    let attempt = {};
    question.map((q: any, i) => {
        attempt = { ...attempt, [q.id]: isAns[i] }
    })
    const handleChange = (e: any, i: any) => {

        const inputdata = [...isAns]
        inputdata[i] = e.target.value;
        setIsAns(inputdata)
        //console.log(e.target.value)

    };

    const submit = () => {

        let data = {

            'attemptans': attempt,
            'quiz_id': quizId,

        }
        fetch(`${baseUrl}${api.quizAttempt}`, {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(response => {
                if (response.status === 200) {
                    setModalOpen(true);
                    //window.location.href = `/dashboard/mycourse/${props.courseId}/quiz/result`;

                }
                else {
                    throw Error([response.status, response.statusText].join(' '));
                }
            })

            .catch(error => console.log('error', error));




    };

    return (

        <>
            <QuizResult
                open={modalOpen}
                onClose={() => {
                    setModalOpen(false)
                }}
                courseId={props.courseId}
            />
            <IonContent>
                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>

                            <div className='text-center mt-3'>Total Question -- {question.length} & Quiz will finish {test_end}</div>
                            <div className='text-center mt-3'><h1>{time}</h1></div>
                        </IonCardTitle>
                    </IonCardHeader>

                    <IonCardContent>

                        {question.slice(0,).map((qstn: any, i: any) =>

                            <IonCard key={i.toString()}>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <div className='mb-2'>
                                            {i + 1} / {question.length}
                                        </div>

                                        {qstn.title}
                                        <h2 dangerouslySetInnerHTML={{ __html: qstn.q_content }}></h2>

                                        <>
                                            {
                                                (qstn.question_type != 'MCQ') ?
                                                    <>
                                                        <IonItem>
                                                            <IonInput name='attemptans' value={isAns[i]} placeholder="enter answer" onIonChange={(e) => handleChange(e, i)}></IonInput>

                                                        </IonItem>
                                                    </> :
                                                    <>
                                                        <IonList>

                                                            <IonRadioGroup name='attemptans' value={isAns[i]} onIonChange={(e) => handleChange(e, i)} >
                                                                {qstn.option.map((op: any, i: any) => {
                                                                    return (

                                                                        <IonItem key={i}>
                                                                            <IonLabel >{op.option}</IonLabel>
                                                                            <IonRadio slot="start" value={op.option} ></IonRadio>

                                                                        </IonItem>

                                                                    );
                                                                })}
                                                            </IonRadioGroup>
                                                        </IonList>
                                                    </>
                                            }


                                        </>
                                    </IonCardTitle>
                                </IonCardHeader>
                            </IonCard>


                        )}

                        <div className='text-center mt-3 mb-5'>
                            <IonButton id="quiz_submit" fill="outline" onClick={submit}>submit</IonButton>
                        </div>
                    </IonCardContent>

                </IonCard>


            </IonContent>
        </>
    );
};

const QuizResult: React.FC<{
    onClose: () => void;
    open: boolean;
    courseId: string;
}> = (props) => {
    const [point, setPoint] = useState('');
    const [total_question, setTotalQuestion] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    const [user, setUser] = useState('');
    useEffect(() => {
        fetch(`${baseUrl}${api.quizResult}${localStorage.getItem('quiz_id')}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                // setResult(result)
                setPoint(result.map((item: any) => (item.total_point)))
                setTotalQuestion(result.map((item: any) => (item.total_question)))
                setQuiz_name(result.map((item: any) => (item.quiz_name)))
                setUser(result.map((item: any) => (item.name)))

            })

    }, []);
    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">Update Topic</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        gap: '1.5rem',
                    }}
                >



                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>

                <Button color="secondary" variant="contained" href={`/dashboard/mycourse/${props.courseId}`}>
                    Back to Course
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default QuizQuestion;
