import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import { Editor } from '@tinymce/tinymce-react';
import baseUrl, { api } from '../../pages/Urls';
import { Collapse } from 'react-bootstrap'
const AddTopicArticle: React.FC<{
    onClose: () => void;
    fetchViewTopic: () => void;
    open: boolean;
    topic_name: string
}> = (props) => {

    const [name, setName] = useState('');
    const [content, setContent] = useState('');

    const addTopicArticle = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'name': name,
                'content': content,
                'topic_name': props.topic_name,

            }
            fetch(`${baseUrl}${api.addTopicArticle}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),

            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create topic article Successfully');
                        props.fetchViewTopic();
                        props.onClose();
                        setName('');
                        setContent('');


                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }

    }
    return (
        <Collapse in={props.open}>
            <div className="container mt-5 mb-5 ">

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">add Topic Article</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <IonCardContent>


                        <IonItem>
                            <IonLabel position="stacked">Name</IonLabel>
                            <IonInput type="text"
                                name="name"
                                placeholder="name..."
                                onIonChange={(e: any) => setName(e.target.value)}
                                clearInput></IonInput>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked" >Content</IonLabel>
                            <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                <Editor
                                    textareaName="content"
                                    value={content}
                                    onEditorChange={(content: any, editor: any) => setContent(content)}
                                    init={{
                                        height: 500,
                                        width: 1030,
                                        menubar: true,
                                        plugins: [

                                            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                        ],

                                        toolbar: 'undo redo | blocks | ' +
                                            'bold italic forecolor | alignleft aligncenter ' +
                                            'alignright alignjustify | bullist numlist outdent indent | ' +
                                            'removeformat | help',
                                        content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                    }}
                                />
                            </div>

                        </IonItem>

                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="danger" onClick={props.onClose}>Cancel</IonButton>
                            <IonButton color="primary" onClick={addTopicArticle}>
                                Add
                            </IonButton>

                        </div>
                    </IonCardContent>
                </IonCard>

            </div>

        </Collapse>
    );
}
export default AddTopicArticle;
