import { IonLabel, IonSegment, IonSegmentButton, IonContent, IonPage } from '@ionic/react';
import { Route } from 'react-router-dom';
import { useHistory } from "react-router";

const TeacherNav: React.FC<{
    pathname: string
}> = (props) => {
    const history = useHistory()

    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }

    return (

        <IonSegment onIonChange={segmentChangeHandler} scrollable value={props.pathname}>
            <IonSegmentButton value="/dashboard/course">Course</IonSegmentButton>
            <IonSegmentButton value="/dashboard/my/course">My Course</IonSegmentButton>

        </IonSegment>

    );
};

export default TeacherNav;
