import {
    IonButton, IonButtons, IonHeader, IonTitle, IonToolbar, IonContent,
    IonIcon,
    IonItem,
    IonLabel,
    IonPopover,
    IonRouterLink,
} from '@ionic/react';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Menu,
} from '@mui/material';
import {
    exitOutline,
    personCircle,
    settingsOutline,
} from "ionicons/icons";
import { Link } from 'react-router-dom';
import { useHistory } from "react-router";
import React, { useState, useRef } from "react";
import baseUrl, { api } from '../pages/Urls';
const HeaderButton: React.FC = () => {

    const [open, setOpen] = useState(false);
    const history = useHistory();
    const logout = () => {
        localStorage.removeItem("token");
    };
    return (

        <div className="dropdown">
            <button type="button" className="btn btn-primary dropdown-toggle" data-bs-toggle="dropdown">
                <IonIcon slot="icon-only" icon={personCircle}></IonIcon>
            </button>
            <ul className="dropdown-menu">
                <li class="dropdown-item">Setting</li>

            </ul>
        </div>


    );
};
const ChangePassword: React.FC<{
    onClose: () => void;
    logout: () => void;
    open: boolean;

}> = (props) => {
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [error, setError] = useState("");

    const update = () => {
        let data = {
            'password': password,
            'email': email,
            'token': localStorage.getItem("token"),
        }
        fetch(`${baseUrl}${api.changePassword}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Password Changed Successfully');

                    props.logout();

                }

            })
            .catch(error => console.log('error', error));


    }
    return (
        <Dialog open={props.open}
            disableEnforceFocus={true}>
            <DialogTitle textAlign="center">Change Password</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        gap: '1.5rem',
                    }}
                >

                    <TextField sx={{ mt: 3 }}
                        type="text"
                        label="Email"
                        variant="filled"
                        focused
                        value={email}
                        onChange={(e: any) => setEmail(e.target.value)} />
                    <TextField sx={{ mt: 3 }}
                        type="password"
                        label="New Password"
                        variant="filled"
                        focused
                        value={password}
                        onChange={(e: any) => setPassword(e.target.value)} />

                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={update}>
                    Save
                </Button>
            </DialogActions>
        </Dialog>
    );
}
export default HeaderButton;
