import {
    IonContent,
    IonItem,
    IonLabel,
    IonCard,
    IonAccordion,
    IonAccordionGroup,
    IonText,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';

import { Link } from "react-router-dom";

const ViewUserCourse: React.FC<{
    quiz: any[],
    topic: any[],
    course_name: string,
    courseId: string,


}> = (props) => {


    var today = new Date();
    const current_date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
    const startQuiz = () => {
        props.quiz.map((item: any) => {
            if (item.date == current_date) {
                alert('Sorry date expaire')
            }
            if (item.ahistory == null) {
                alert('opps! You attempted Already')
            }

        })


    }
    return (
        <>
            <IonContent fullscreen={true}>

                <IonText color="primary">
                    <h1 className='mx-2'>{props.course_name}</h1>
                </IonText>

                <>

                    <IonCard >

                        <IonAccordionGroup>
                            <IonAccordion value="first">
                                <IonItem slot="header" color="light">
                                    <IonLabel>Quiz</IonLabel>
                                </IonItem>

                                <div className="ion-padding" slot="content">
                                    {props.quiz.map((item: any, i: any) =>
                                        <h5 key={i.toString()}>
                                            <Link className='text-decoration-none'

                                                to={item.date == current_date && item.ahistory == null ?
                                                    `/dashboard/mycourse/${props.courseId}/quiz/${item.id}` : `/dashboard/mycourse/${props.courseId}`} >
                                                <span onClick={startQuiz}>{item.quiz_name}</span>


                                            </Link>
                                            {item.date == current_date ?
                                                <p className='text-info'>New</p> : <p className='text-danger'>Date expired</p>
                                            }
                                        </h5>

                                    )}
                                </div>
                            </IonAccordion>
                        </IonAccordionGroup>

                    </IonCard>
                    {props.topic.map((item: any, i: any) =>
                        <IonCard key={i.toString()}>

                            <IonAccordionGroup>
                                <IonAccordion value="first">
                                    <IonItem slot="header" color="light">
                                        <IonLabel>{item.topic_name}</IonLabel>
                                    </IonItem>

                                    <div className="ion-padding" slot="content">
                                        {item.topic_article.length == 0 && item.topic_content.length == 0 ? 'No data preview' :
                                            <>
                                                {item.topic_article.map((ta: any, i: any) =>
                                                    <IonItem key={i.toString()}>
                                                        <Link className='text-decoration-none'
                                                            to={{
                                                                pathname: `/dashboard/mycourse/${item.course_id}/article/${ta.id}`,
                                                                state: {
                                                                    artname: ta.name,
                                                                    artcontent: ta.content,
                                                                    course_id: item.course_id,

                                                                }
                                                            }} >
                                                            {ta.name}</Link>

                                                    </IonItem>



                                                )}
                                                {item.topic_content.map((tc: any, i: any) =>
                                                    <IonItem key={i.toString()}>
                                                        <IonLabel>file</IonLabel>
                                                    </IonItem>

                                                )}
                                            </>

                                        }
                                    </div>
                                </IonAccordion>
                            </IonAccordionGroup>

                        </IonCard>
                    )}
                </>

            </ IonContent>

        </>
    );


}




export default ViewUserCourse;
