import React, { useState, useEffect } from 'react';
import { useLocation, useHistory } from 'react-router-dom';
const QuizQuestion: React.FC = () => {
    const location = useLocation();
    const history = useHistory();
    const [question, setQuestion] = useState([]);
    const [locationState, setLocationState] = useState({

        quizLevel: [],

    });

    useEffect(() => {
        if (location.state) {
            let _state1 = location.state as any

            window.localStorage.setItem('quiz_level', JSON.stringify(_state1))

        }
    }, [location])

    useEffect(() => {

        const data = window.localStorage.getItem('quiz_level');
        if (data !== null) {
            setLocationState(JSON.parse(data))

        };
    }, [])
    useEffect(() => {

        setQuestion
        {
            locationState.quizLevel.map((item: any) => {
                {
                    item.lquestion.map((lq: any) =>
                        <p>{lq.title}</p>
                    )
                }
            }
            )
        }

    }, [])



    return (

        <>

            {locationState.quizLevel.map((item: any) => {
                <h1>{
                    item.level_name
                }</h1>
            }
            )}

        </>
    )
}
export default QuizQuestion;
