import {

    IonPage,
    IonContent,
    IonTitle,
    IonSegment,
    IonSegmentButton,
    IonRouterOutlet

} from "@ionic/react";
import { Redirect } from 'react-router-dom';
import { Route, useHistory } from "react-router";
import Group from '../components/admin/Group';
import AdminNav from './nav/AdminNav';
import UserNav from './nav/UserNav';
import Nav from './nav/Nav';
import Articles from '../components/admin/articles/Articles';
import Quiz from '../components/admin/quiz/Quiz';
import Question from '../components/admin/quiz/Question';
import TrashArticle from '../components/admin/articles/TrashArticle';
import Uquiz from '../components/user/uquiz/Uquiz';
import Blog from '../components/user/blog/Blog';
import ViewBlog from '../components/user/blog/ViewBlog';
import ViewQuiz from '../components/user/uquiz/ViewQuiz';
import ViewQuestion from '../components/user/uquiz/ViewQuestion';
import QuizResult from '../components/user/uquiz/QuizResult';
import { IonReactRouter } from '@ionic/react-router';
import Course from '../components/admin/quiz/Course';
import Topic from '../components/admin/quiz/Topic';
import Level from '../components/admin/quiz/Level';
import TeacherNav from "./nav/TeacherNav";


const Dashboard: React.FC = () => {

    return (
        <IonPage>

            {/* {
                (localStorage.getItem('role') == 'ar_mgmt' ||
                    localStorage.getItem('role') == 'ar_admin' ||
                    localStorage.getItem('role') == 'ar_staff1' ||
                    localStorage.getItem('role') == 'ar_staff2') ? (
                    <Route path="/dashboard" component={AdminDashboard} />
                ) :
                    <Route path="/dashboard" component={UserDashboard} />


            } */}

            if ({localStorage.getItem('role') == 'ar_mgmt' ||
                localStorage.getItem('role') == 'ar_admin' ||
                localStorage.getItem('role') == 'ar_staff1' ||
                localStorage.getItem('role') == 'ar_staff2'}) {
                <Route path="/dashboard" component={AdminDashboard} />
            } elseif (localStorage.getItem('role') == 'ar_teacher')  {
                <Route path="/dashboard" component={TeacherDashboard} />
            } else {
                <Route path="/dashboard" component={UserDashboard} />
            }




        </IonPage>
    );
};

const AdminDashboard: React.FC = () => {
    const history = useHistory()
    const pathname = history.location.pathname
    return (



        <IonContent>
            <AdminNav pathname={pathname} />

            <Route path="/dashboard/article" component={Articles} />
            <Route path="/dashboard/group" component={Group} />
            <Route path="/dashboard/course" component={Course} />
            <Route path="/dashboard/topic" component={Topic} />
            <Route path="/dashboard/quiz" component={Quiz} />
            <Route path="/dashboard/level" component={Level} />
            <Route path="/dashboard/question" component={Question} />
            <Route path="/dashboard/trash" component={TrashArticle} />
            <Route exact path="/dashboard">
                <Redirect to="/dashboard/article" />
            </Route>

        </IonContent>


    );
};
const TeacherDashboard: React.FC = () => {
    const history = useHistory()
    const pathname = history.location.pathname
    return (

        <IonContent>
            <TeacherNav pathname={pathname} />


            <Route path="/dashboard/course" component={Course} />

            <Route exact path="/dashboard">
                <Redirect to="/dashboard/article" />
            </Route>

        </IonContent>


    );
};
const UserDashboard: React.FC = () => {
    const history = useHistory()
    const pathname = history.location.pathname;

    return (
        <>

            <UserNav pathname={pathname} />
            <IonContent className="ion-padding">
                <IonRouterOutlet>
                    <Route path="/dashboard/blog" component={Blog} />
                    <Route path="/dashboard/view/blog/:id" component={ViewBlog} />
                    <Route path="/dashboard/quiz">
                        <UserQuiz />
                    </Route>

                    <Route exact path="/dashboard">
                        <Redirect to="/dashboard/blog" />
                    </Route>

                </IonRouterOutlet>
            </IonContent>
        </>

        // <IonPage>
        //     <IonTitle>user dashboard</IonTitle>
        // </IonPage>

    );
};
const UserQuiz: React.FC = () => {


    return (
        <>

            <IonRouterOutlet>
                <IonContent>
                    <Route exact path="/dashboard/quiz"  >
                        <Uquiz />
                    </Route>
                    <Route exact path="/dashboard/quiz/:id" >
                        <ViewQuestion />
                    </Route>
                    <Route exact path="/dashboard/quiz/result/:id">
                        <QuizResult />
                    </Route>
                </IonContent>
            </IonRouterOutlet>
        </>



    );
};

export default Dashboard;
