import {
    IonButton, IonButtons, IonHeader, IonTitle, IonToolbar, IonContent,
    IonIcon,
    IonItem,
    IonLabel,
    IonPopover,
    IonRouterLink,
} from '@ionic/react';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Menu,
} from '@mui/material';
import {
    exitOutline,
    personCircle,
    settingsOutline,
} from "ionicons/icons";
import { Link } from 'react-router-dom';
import { useHistory } from "react-router";
import React, { useState, useRef } from "react";
import baseUrl, { api } from '../pages/Urls';
const HeaderButton: React.FC = () => {


    const history = useHistory();
    const logout = () => {
        localStorage.removeItem("token");
    };
    return (

        <IonButtons slot="secondary">

            <IonButton id="profile-trigger">
                <IonIcon slot="icon-only" icon={personCircle}></IonIcon>
            </IonButton>
            <IonPopover
                trigger="profile-trigger"
                triggerAction="click"
                showBackdrop={false}
            >
                <IonContent>
                    <IonItem>
                        <IonIcon slot="end" icon={personCircle}></IonIcon>
                        <IonLabel>{localStorage.getItem("username")}</IonLabel>
                    </IonItem>
                    <IonItem >
                        <IonIcon slot="end" icon={settingsOutline}></IonIcon>
                        <Link className='text-decoration-none' to={`/dashboard/change`} style={{ color: 'black' }}> Setting</Link>

                    </IonItem>

                    <IonItem href="" onClick={logout} >
                        <IonIcon slot="end" icon={exitOutline}></IonIcon>
                        <IonLabel>Logout</IonLabel>
                    </IonItem>
                </IonContent>
            </IonPopover>

        </IonButtons>

    );
};

export default HeaderButton;
