import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
import { Typography } from '@mui/material';
type CourseData = {
    id: string;
    course_name: string;
    description: string;

};


const Course: FC = () => {
    const [course, setCourse] = useState([]);

    const [course_name, setCourse_name] = useState('');
    const [description, setDescription] = useState('');
    const [id, setId] = useState('');
    const [crs_inst, setCrs_inst] = useState([]);
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const fetchCourse = () => {
        fetch(`${baseUrl}${api.allCourse}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCourse(resp.reverse())
            })
    }
    useEffect(() => {
        fetchCourse();

    }, [])

    const columns = useMemo<MRT_ColumnDef<CourseData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                enableColumnOrdering: false,
                size: 80,
            },
            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'description',
                header: 'Description',

            },

        ],
        [],
    );
    const editCourse = (id: any) => {

        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {

                setId(result.article.id);

                setCrs_inst(

                    result.article.group.map((grp: any) => (grp.name))

                );
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateArticle = () => {
        let data = {
            'course_name': course_name,
            'description': description,
            'crs_inst': crs_inst,
        }
        fetch(`${baseUrl}${api.updateCourse}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {

                    fetchCourse();
                    setOpen(false)

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteCourse = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteCourse}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        fetchCourse();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }

    return (
        <IonContent>
            <div className="container mt-5">
                <Collapse in={open}>
                    <div id="collapseID">
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Article</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Title</IonLabel>
                                        <IonInput type="text"
                                            name="title"
                                            value={title}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setTitle(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Slug</IonLabel>
                                        <IonInput type="text"
                                            name="slug"
                                            value={slug}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setSlug(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked" >Content</IonLabel>
                                        <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                            <Editor
                                                textareaName="content"
                                                value={content}
                                                onEditorChange={(content: any, editor: any) => setContent(content)}
                                                init={{
                                                    height: 500,
                                                    width: 1030,
                                                    menubar: true,
                                                    plugins: [

                                                        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                    ],

                                                    toolbar: 'undo redo | blocks | ' +
                                                        'bold italic forecolor | alignleft aligncenter ' +
                                                        'alignright alignjustify | bullist numlist outdent indent | ' +
                                                        'removeformat | help',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                                }}
                                            />
                                        </div>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Group</IonLabel>
                                        <IonSelect
                                            name="gname"
                                            value={gname}
                                            onIonChange={(e: any) => setGname(e.target.value)}

                                            placeholder="Select groups" multiple={true}>
                                            {group.map((item: any, i: any) => (
                                                < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                            ))}
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Status</IonLabel>

                                        <IonSelect
                                            name="status"
                                            value={status}
                                            onIonChange={(e: any) => setStatus(e.target.value)}
                                        >
                                            {(localStorage.getItem('role') == 'ar_mgmt') ?
                                                <>
                                                    <IonSelectOption>P</IonSelectOption>
                                                    <IonSelectOption>D</IonSelectOption>
                                                    <IonSelectOption>U</IonSelectOption>
                                                </>
                                                :
                                                <IonSelectOption>U</IonSelectOption>

                                            }
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Date</IonLabel>
                                        <IonInput type="date"
                                            name="date"
                                            value={date}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setDate(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>

                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="danger" onClick={() => setOpen(false)}>
                                            Cancel
                                        </IonButton>
                                        <IonButton color="primary" onClick={updateArticle}>
                                            Save
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </div>
                </Collapse>
                <h1 className='text-center mb-2'>List of Courses</h1>
                <MaterialReactTable
                    columns={columns}
                    data={course}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    editCourse(row.id)
                                    setOpen(!open)
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteCourse(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-Course">
                            <IconButton aria-expanded={collapse} onClick={() => setCollapse(!collapse)}>
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />

            </div>
        </IonContent>

    );

};



export default Course;
