import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonText,
    IonTextarea,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import baseUrl, { api } from '../../../pages/Urls';
import TextField from '@mui/material/TextField';
const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};

const Instructor: React.FC = () => {

    const [instructor, setInstructor] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [error, setError] = useState("");
    const [search, setSearch] = useState('');
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);
    useEffect(() => {
        fetch(`${baseUrl}${api.allInstructor}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setInstructor(resp.reverse())
            })
    }, []);


    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - instructor.length) : 0;

    const addInstructor = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'name': name,
                'email': email,

            }
            fetch(`${baseUrl}${api.addInstructor}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Course Successfully');
                        window.location.href = '/dashboard/instructor';
                        setError("");

                    }
                    else {
                        if (!name || !email) {
                            setError("Please fill all input Field");
                        }

                    }
                })

                .catch(error => console.log('error', error));

        }
    }


    const deleteInstructor = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteInstructor}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        window.location.href = '/dashboard/instructor';

                    }

                })
                .catch(error => console.log('error', error));
        }
    }


    return (
        <IonContent>
            <div className="container mt-5">
                <Paper sx={{ width: '100%', mb: 2 }}>
                    <p className="h1 text-center">List of Instructors</p>
                    <div className="input-group rounded">
                        <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => setSearch(e.target.value)} />
                    </div>
                    <div className="float-end">
                        <Button onClick={handleOpen}>
                            <i className="bi bi-plus-circle-fill" title="add"></i>
                        </Button>
                    </div>
                    <Modal
                        open={open}
                        onClose={handleClose}
                        aria-labelledby="modal-modal-title"
                        aria-describedby="modal-modal-description"
                    >
                        <Box sx={style}>
                            <div className="text-end">
                                <Button onClick={handleClose} variant="text">X</Button>
                            </div>
                            <Typography sx={{ mt: 3 }} id="modal-modal-title" variant="h6" component="h2">
                                Add Instructor
                            </Typography>
                            <TextField sx={{ mt: 3 }}
                                type="text"
                                label="Instructor Name"
                                variant="filled"
                                focused
                                onChange={(e: any) => setName(e.target.value)} />

                            <TextField sx={{ mt: 3 }}
                                type="email"
                                label="Instructor Email"
                                variant="filled"
                                focused
                                onChange={(e: any) => setEmail(e.target.value)} />
                            <div>
                                <Button sx={{ mt: 3 }} variant="outlined" onClick={addInstructor}>Add</Button>
                            </div>
                        </Box>
                    </Modal>


                    <TableContainer>
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Id</TableCell>
                                    <TableCell >Instructor Name</TableCell>
                                    <TableCell >Email</TableCell>
                                    <TableCell >Action</TableCell>

                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {(rowsPerPage > 0
                                    ? instructor.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : instructor
                                ).filter((item: any) => {
                                    return search.toLowerCase() === '' ? item :
                                        item.name.toLowerCase().includes(search);

                                }).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell component="th" scope="row">{item.id}</TableCell>
                                        <TableCell >{item.name}</TableCell>
                                        <TableCell >{item.email}</TableCell>

                                        <TableCell >

                                            <button onClick={() => deleteInstructor(item.id)} >
                                                <i className="bi bi-trash" title="delete"></i>
                                            </button>
                                        </TableCell>
                                    </TableRow>
                                ))}


                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[10, 15, 20]}
                        component="div"
                        count={instructor.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />

                </Paper>

            </div>
        </IonContent>
    );
}

export default Instructor;
