import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import MaterialReactTable, {
    MRT_ColumnDef,

} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';

type leveldata = {
    id: string;
    course_name: string;
    level_name: string;

};


const Level: FC = () => {
    const [level, setLevel] = useState([]);
    const [levelData, setLevelData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const fetchlevel = () => {
        fetch(`${baseUrl}${api.allLevel}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setLevel(resp.reverse())
            })
    }
    useEffect(() => {
        fetchlevel();
    }, [])

    const columns = useMemo<MRT_ColumnDef<leveldata>[]>(
        () => [


            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },

            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },

        ],
        [],
    );
    const editLevel = (id: any) => {

        fetch(`${baseUrl}${api.showLevel}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setLevel_name(result.level.level_name);
                setId(result.level.id);
                setLTopic(

                    result.level.ltopic.map((lt: any) => (lt.topic_name))

                );
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateLevel = () => {
        let data = {
            'ltopic': ltopic,
            'level_name': level_name,


        }
        fetch(`${baseUrl}${api.updateLevel}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    props.fetchShowCourse();
                    setCollapse(false);

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteLevel = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteLevel}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchlevel();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Levels</h1>
                <MaterialReactTable
                    columns={columns}
                    data={level}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableRowNumbers
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    setLevelData(row.original)
                                    setModalOpen(true);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteLevel(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}

                />
                <EditTopic
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)

                    }}
                    fetchlevel={() => fetchlevel()}
                    levelData={levelData}
                />
            </div>
        </IonContent>

    );

};

const EditTopic: FC<{
    onClose: () => void;
    fetchlevel: () => void;
    open: boolean;
    levelData: any

}> = (props) => {
    const [level_name, setLevel_name] = useState('');
    const [error, setError] = useState("");
    useEffect(() => {
        setLevel_name(props.levelData.level_name);
    }, [props.levelData])

    const updateLevel = () => {
        let data = {
            'level_name': level_name,
        }
        fetch(`${baseUrl}${api.updateLevel}${props.levelData.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    props.fetchlevel();
                    props.onClose();

                }

            })
            .catch(error => console.log('error', error));


    }
    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">Update Level</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        gap: '1.5rem',
                    }}
                >

                    <TextField sx={{ mt: 3 }}
                        type="text"
                        label="Lvel Name"
                        variant="filled"
                        focused
                        value={level_name || ''}
                        onChange={(e: any) => setLevel_name(e.target.value)} />

                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={updateLevel}>
                    Save
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default Level;
