import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import { Redirect } from 'react-router-dom';
import baseUrl, { api } from '../../pages/Urls';


const QuizLevel: React.FC = () => {

    const [quizLevel, setQuizLevel] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(1);
    const [redirectToReferrer, setRedirectToReferrer] = React.useState(false);
    const [quiz_id, setQuiz_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [mark, setMark] = useState('');
    const [duration, setDuration] = useState('');
    const [status, setStatus] = useState('');
    const [quiz, setQuiz] = useState([])
    const [quizLvel_id, setQuizLvel_id] = useState('')
    const [question, setQuestion] = useState('')
    const [questionType, setQuestionType] = useState('')
    const [answers, setAnswers]: any[] = useState([]);
    ////////
    const [id, setId] = useState('');
    const [isOpen, setIsOpen] = useState(false);
    /////
    const handleInputChange = (e: any, index: any) => {
        const { name, value } = e.target;
        const list = [...answers];
        list[index][name] = value;
        setAnswers(list);
    };


    const handleRemoveClick = (index: any) => {
        const list = [...answers];
        list.splice(index, 1);
        setAnswers(list);
    };


    const handleAddClick = () => {
        setAnswers([...answers]);
    };
    /////
    useEffect(() => {
        fetch(`${baseUrl}${api.allQuizlevel}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setQuizLevel(resp.reverse())
            })
    }, [])
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - quizLevel.length) : 0;


    const editQuizLevel = (id: any) => {

        fetch(`${baseUrl}${api.editQuizlevel}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setLevel_name(result.quizLevel.level_name);
                setMark(result.quizLevel.mark);
                setDuration(result.quizLevel.duration);
                setStatus(result.quizLevel.status);
                setId(result.quizLevel.id)
            })
            .catch(function (error) {
                console.log(error);
            })

    }

    const updateQuizLevel = () => {
        let data = {
            'level_name': level_name,
            'duration': duration,
            'mark': mark,
            'status': status,
        }
        fetch(`${baseUrl}${api.updateQuizlevel}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Quiz level Successfully');
                    window.location.href = '/quizlevel';

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteQuizLevel = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuizlevel}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        setRedirectToReferrer(true);
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    const addQuestion = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'quizlevel_id': quizLvel_id,
                'question': question,
                'question_type': questionType,

            }
            fetch(`${baseUrl}${api.addQuestion}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Quiz level Successfully');
                        window.location.href = '/quizlevel';

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <div className="container mt-5">


            <div id="demo" className="collapse mt-3 mb-3">

                <>
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Quiz Level</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Level Name</IonLabel>
                                    <IonInput type="text"
                                        name="level_name"
                                        value={level_name}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setLevel_name(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Duration</IonLabel>
                                    <IonInput type="number"
                                        name="duration"
                                        value={duration}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setDuration(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Mark Per Question</IonLabel>
                                    <IonInput type="number"
                                        name="mark"
                                        value={mark}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setMark(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Status</IonLabel>

                                    <IonSelect
                                        name="status"
                                        value={status}
                                        onIonChange={(e: any) => setStatus(e.target.value)}
                                    >

                                        <>
                                            <IonSelectOption>P</IonSelectOption>
                                            <IonSelectOption>D</IonSelectOption>

                                        </>

                                    </IonSelect>

                                </IonItem>

                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="primary" onClick={updateQuizLevel}>
                                        Update-Quiz-Level
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </>

            </div>
            <div id="add" className="collapse mt-3 mb-3">
                <>
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">add Question</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Quiz level Id</IonLabel>
                                    <IonInput type="text"
                                        name="id"
                                        value={id}
                                        disabled={true}
                                        onIonChange={(e: any) => setQuizLvel_id(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Question Type</IonLabel>

                                    <IonSelect
                                        name="question_type"
                                        value={questionType}
                                        onIonChange={(e: any) => setQuestionType(e.target.value)}
                                    >

                                        <>
                                            <IonSelectOption>MCQ</IonSelectOption>
                                            <IonSelectOption>SQ</IonSelectOption>

                                        </>

                                    </IonSelect>

                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Question</IonLabel>
                                    <IonInput type="text"
                                        name="question"
                                        placeholder="question..."
                                        onIonChange={(e: any) => setQuestion(e.target.value)}
                                        clearInput></IonInput>
                                </IonItem>
                                <IonText color="primary">
                                    <h2 className="mt-3">Add Question Option</h2>

                                    {answers.map((x: any, i: any) => {

                                        <div className="box">
                                            <input
                                                name="firstName"
                                                placeholder="Enter First Name"
                                                value={x.firstName}
                                                onChange={e => handleInputChange(e, i)}
                                            />
                                            <input
                                                className="ml10"
                                                name="lastName"
                                                placeholder="Enter Last Name"
                                                value={x.lastName}
                                                onChange={e => handleInputChange(e, i)}
                                            />
                                            <div className="btn-box">
                                                {answers.length !== 1 && <button
                                                    className="mr10"
                                                    onClick={() => handleRemoveClick(i)}> <i className="bi bi-dash-circle-fill" title="remove-option" ></i></button>}
                                                {answers.length - 1 === i && <button onClick={handleAddClick}>
                                                    <i className="bi bi-plus-circle-fill" title="add-option" ></i>
                                                </button>}
                                            </div>
                                        </div>

                                    })}
                                    <button type="button" onClick={handleAddClick}>
                                        <i className="bi bi-plus-circle-fill" title="add-option" ></i>
                                    </button>


                                </IonText>
                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="primary" onClick={addQuestion}>
                                        Add Question
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </>

            </div>
            <Paper sx={{ width: '100%', mb: 2 }}>
                <p className="h1 text-center">List of Quiz levels</p>

                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Id</TableCell>
                                <TableCell >Level Name</TableCell>
                                <TableCell >Quiz Id</TableCell>
                                <TableCell >Duration </TableCell>
                                <TableCell >Mark Per Question</TableCell>
                                <TableCell >Status</TableCell>
                                <TableCell >Action</TableCell>

                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {(rowsPerPage > 0
                                ? quizLevel.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                : quizLevel
                            ).map((item: any, i: any) => (
                                <TableRow key={i.toString()}>
                                    <TableCell component="th" scope="row">{item.id}</TableCell>
                                    <TableCell >{item.level_name}</TableCell>
                                    <TableCell >{item.quiz_id}</TableCell>
                                    <TableCell >{item.duration}</TableCell>
                                    <TableCell >{item.mark}</TableCell>
                                    <TableCell >{item.status}</TableCell>

                                    <TableCell >
                                        <button onClick={() => editQuizLevel(item.id)} data-bs-toggle="collapse" data-bs-target="#add">
                                            <i className="bi bi-plus-circle-dotted" title="add-question"></i>
                                        </button>
                                        <button onClick={() => editQuizLevel(item.id)} data-bs-toggle="collapse" data-bs-target="#demo">
                                            <i className="bi bi-pencil-square" title="edit-quiz-level"></i>
                                        </button>

                                        <button onClick={() => deleteQuizLevel(item.id)} >
                                            <i className="bi bi-trash" title="delete"></i>
                                        </button>
                                    </TableCell>
                                </TableRow>
                            ))}


                        </TableBody>
                    </Table>

                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[1, 2, 5]}
                    component="div"
                    count={quizLevel.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </Paper>

        </div>
    );
}

export default QuizLevel;
