import {
    IonCard,
    IonCardContent,
    IonCardHeader,
    IonCardSubtitle, IonLabel,
    IonSegment,
    IonSegmentButton,
    IonCardTitle, IonContent, IonHeader, IonMenu, IonSplitPane, IonTitle, IonToolbar, IonMenuToggle, IonButton, IonItem, IonList, IonRouterOutlet
} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router";

const ViewCourse: React.FC = () => {
    const history = useHistory()
    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }

    const showSplitPane = useBreakpoint('lg')

    return (
        <IonApp>
            <IonReactRouter>
                {showSplitPane
                    ?
                    <IonSplitPane contentId="main">
                        <Menu items={menuItems} />
                        <IonRouterOutlet id="main">
                            {routeComponents}
                        </IonRouterOutlet>
                    </IonSplitPane>
                    :
                    <TabsWrapper items={menuItems}>
                        <IonRouterOutlet id="main">
                            {routeComponents}
                        </IonRouterOutlet>
                    </TabsWrapper>
                }
            </IonReactRouter>
        </IonApp>
    )
}
export default ViewCourse;
