import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
import { Typography } from '@mui/material';
type Group = {
    id: string;
    topic_name: string;
    course_name: string;

};

//nested data is ok, see accessorKeys in ColumnDef below


const Topic: FC = () => {
    const [topic, setTopic] = useState([]);
    const [topicData, setTopicData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const fetchTopic = () => {
        fetch(`${baseUrl}${api.allTopic}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setTopic(resp.reverse())
            })
    }
    useEffect(() => {
        fetchTopic();
    }, [])

    const columns = useMemo<MRT_ColumnDef<Group>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                enableColumnOrdering: false,
                size: 80,
            },

            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },

        ],
        [],
    );

    const deleteTopic = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteTopic}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchTopic();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Topics</h1>
                <MaterialReactTable
                    columns={columns}
                    data={topic}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    setTopicData(row.original)
                                    setModalOpen(true);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteTopic(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}

                />
                <EditTopic
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)

                    }}
                    fetchTopic={() => fetchTopic()}
                    topicData={topicData}
                />
            </div>
        </IonContent>

    );

};

const EditTopic: FC<{
    onClose: () => void;
    fetchTopic: () => void;
    open: boolean;
    topicData: any

}> = (props) => {
    const [topic_name, setTopic_name] = useState('');
    const [error, setError] = useState("");
    useEffect(() => {
        setTopic_name(props.topicData.topic_name);
    }, [props.topicData])

    const updateTopic = () => {
        let data = {
            'topic_name': topic_name,
        }
        fetch(`${baseUrl}${api.updateTopic}${props.topicData.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update topic Successfully');
                    props.fetchTopic();
                    props.onClose();

                }

            })
            .catch(error => console.log('error', error));


    }
    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">Update Topic</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        gap: '1.5rem',
                    }}
                >

                    <TextField sx={{ mt: 3 }}
                        type="text"
                        label="Toppic Name"
                        variant="filled"
                        focused
                        value={topic_name || ''}
                        onChange={(e: any) => setTopic_name(e.target.value)} />

                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={updateTopic}>
                    Save
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default Topic;
