import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,
    IonLabel, IonList, IonRadio, IonRadioGroup, IonInput, IonCheckbox
} from '@ionic/react';

import React, { useState, useEffect, useRef } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewQuestion: React.FC = (props) => {
    type ViewQuestionParams = {
        id: string;
    };
    const [questions, setQuestions] = useState([]);
    const [question, setQuestion] = useState('');
    const [option, setOption]: any[] = useState([]);
    const [total_question, setTotalQuestion] = useState('');
    const [minutes, setMinutes] = useState(1);
    const [seconds, setSeconds] = useState(0);
    const [isAns, setIsAns]: any[] = useState([]);
    const [qtyp, setQtyp] = useState('');
    const [question_id, setQuestion_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [duration, setDuration] = useState(20);
    const [qIdx, setQIdx] = useState(0);
    const { id } = useParams<ViewQuestionParams>();
    const history = useHistory();
    const next = () => {

        if (qIdx + 1 < questions.length) {
            setQIdx(qIdx + 1);
        }

    };
    const previous = () => {

        if (qIdx + 1 < questions.length || qIdx + 1 == questions.length) {
            setQIdx(qIdx - 1);
        }

    };
    const submit = (e: any) => {

        if (window.confirm("want to submit?")) {

            e.preventDefault();
            let data = {

                'attempt_ans': isAns,
                'question_id': question_id,

            }
            fetch(`${baseUrl}${api.attemptQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {

                        window.location.href = '/dashboard/quiz';

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));


        }
    };
    const handleChange = (onIonChangeValue: any, qIdx: any) => {
        const inputdata = [...isAns]
        inputdata[qIdx] = onIonChangeValue.target.value;
        setIsAns(inputdata)
        console.log(onIonChangeValue.target.value)
    };



    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setQuestions(result);
                setQuestion(result.map((op: any) => (op.question)))
                setQuestion_id(result.map((op: any) => (op.id)))
                setQtyp(result.map((op: any) => (op.question_type)))
                setTotalQuestion(result.length);
                setOption(result.map((op: any) => (op.option)));
            })


            .catch(function (error) {
                console.log(error);
            })


    }, [])
    //console.log('Q', isAns);

    useEffect(() => {
        let myInterval = setInterval(() => {
            if (seconds > 0) {
                setSeconds(seconds - 1);
            }
            if (seconds === 0) {
                if (minutes === 0) {
                    clearInterval(myInterval)
                } else {
                    setMinutes(minutes - 1);
                    setSeconds(59);
                }
            }
        }, 1000)
        return () => {
            clearInterval(myInterval);
        };
    });
    //console.log('op', option)
    return (
        <>
            <IonContent>

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonGrid>
                                <IonRow>
                                    <IonCol>Question: {qIdx + 1} / {total_question}</IonCol>
                                    <IonCol>
                                        {minutes === 0 && seconds === 0
                                            ? ''
                                            : <h1> {minutes < 10 ? `0${minutes}` : minutes}:{seconds < 10 ? `0${seconds}` : seconds}</h1>
                                        }
                                    </IonCol>
                                </IonRow>
                            </IonGrid>
                        </IonCardTitle>
                    </IonCardHeader>

                    <IonCardContent>
                        <>
                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>{question[qIdx]}</IonCardTitle>
                                </IonCardHeader>
                                <IonCardContent>

                                    {
                                        (qtyp[qIdx] != 'MCQ') ?
                                            <>
                                                <IonItem>
                                                    <IonInput name='attempt_ans' value={isAns[qIdx]} placeholder="enter answer" onIonChange={(e) => handleChange(e, qIdx)}></IonInput>
                                                </IonItem>
                                            </> :
                                            <>
                                                <IonList>

                                                    <IonRadioGroup name='attempt_ans' value={isAns[qIdx]} onIonChange={(e) => handleChange(e, qIdx)} >
                                                        {option[qIdx] !== undefined ? option[qIdx].map((op: any, i: any) => {
                                                            return (

                                                                <IonItem key={i}>
                                                                    <IonLabel >{op.option}</IonLabel>
                                                                    <IonRadio slot="start" value={op.option} ></IonRadio>
                                                                </IonItem>

                                                            );
                                                        }) : undefined}
                                                    </IonRadioGroup>
                                                </IonList>
                                            </>
                                    }

                                    <div className='text-center mt-3 mb-2'>

                                        {(qIdx + 1 == 1) ? '' : <IonButton fill="outline" onClick={previous}>previous</IonButton>}
                                        {(qIdx + 1 == questions.length) ? '' : <IonButton fill="outline" onClick={next}>next</IonButton>}
                                        {(qIdx + 1 == questions.length) ? <IonButton fill="outline" onClick={submit}>submit</IonButton> : ''}
                                    </div>

                                </IonCardContent>

                            </IonCard>
                        </>
                    </IonCardContent>




                </IonCard>

            </IonContent>

        </>

    );
}
export default ViewQuestion;
