import {
    IonContent,
    IonButton,
    IonCardContent,
    IonText,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { Route, useParams, useLocation } from "react-router";
import baseUrl, { api } from '../../Urls';

const QuizResult: React.FC = () => {


    const [point, setPoint] = useState('');
    const [total_question, setTotalQuestion] = useState('');
    const [result, setResult] = useState([]);

    useEffect(() => {
        fetch(`${baseUrl}${api.quizResult}${localStorage.getItem('quiz_id')}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setResult(result)
                setPoint(result.total_points)
                setTotalQuestion(result.total_question);

            })

    }, []);
    //const count = point.filter(x => x == 1).length;
    return (

        <IonContent >
            <IonCard>
                <IonCardHeader>
                    <div className='text-center mb-3'>
                        <IonCardTitle>
                            <IonText color="primary">
                                <h1>Congratulations!</h1>

                            </IonText>

                        </IonCardTitle>
                        <IonCardSubtitle>You have finished online Quiz Successfully  </IonCardSubtitle>
                    </div>

                </IonCardHeader>

                <IonCardContent>
                    <div className='text-center'>
                        <h1>Correct answer: {point} / {total_question}</h1>

                    </div>
                </IonCardContent>
                <div className='text-center mt-3 mb-5'>
                    <IonButton fill="outline" href="/dashboard">Dashboard</IonButton>
                </div>
            </IonCard>
        </IonContent>
    )
}
export default QuizResult;
