import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
    IonList,
    IonRadioGroup,
    IonRadio,

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
type quizData = {
    id: string;
    course_name: string;
    quiz_name: string;
    status: string;




};


const Quiz: FC = () => {
    const [quiz, setQuiz] = useState([]);
    const [quiz_name, setQuiz_name] = useState('');
    const [id, setId] = useState('');
    const [status, setStatus] = useState('');
    const [date, setDate] = useState('');
    const [search, setSearch] = useState('');
    const [level, setLevel] = useState([]);
    const [error, setError] = useState("");
    const [open, setOpen] = useState(false);
    const [inputList, setinputList] = useState([{ level_name: '', qcount: 0 }]);
    const handleinputchange = (e: any, index: any) => {
        const { name, value } = e.target;
        const list: any[] = [...inputList];
        list[index][name] = value;
        setinputList(list);


    }
    const handleaddclick = () => {

        const abc = [...inputList, { level_name: '', qcount: 0 }]

        setinputList(abc);
    }
    const handleremove = (index: any) => {
        const list = [...inputList];
        list.splice(index, 1);
        setinputList(list);
    }
    const fetchQuiz = () => {
        fetch(`${baseUrl}${api.allQuiz}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setQuiz(resp.reverse())
            })
    }
    useEffect(() => {
        fetchQuiz();
    }, [])

    const columns = useMemo<MRT_ColumnDef<quizData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                size: 80,
            },

            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'quiz_name',
                header: 'Quiz Name',

            },
            {
                accessorKey: 'status',
                header: 'Status',

            },


        ],
        [],
    );



    const editQuiz = (id: any) => {

        fetch(`${baseUrl}${api.showQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setQuiz_name(result.quiz.quiz_name);
                setStatus(result.quiz.status);
                setDate(result.quiz.date);
                setLevel(result.level);
                setId(result.quiz.id)
                let levelList: any[] = [];
                {
                    result.quiz.qlevel.map((item: any, i: any) =>
                        levelList.push({
                            level_name: item.level_name,
                            qcount: item.qcount
                        })
                    )
                }
                setinputList([...levelList]);

            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateQuiz = () => {
        let data = {
            'quiz_name': quiz_name,
            'status': status,
            'date': date,
            'inputlist': inputList,
        }
        fetch(`${baseUrl}${api.updateQuestion}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update question Successfully');
                    fetchQuiz();
                    setOpen(false)

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteQuestion = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuestion}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchQuiz();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    //console.log(questionData)
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Questions</h1>
                <Collapse in={open}>
                    <div id="collapseID">
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Question</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Question Type</IonLabel>

                                        <IonSelect
                                            name="question_type"
                                            value={questionType}
                                            onIonChange={(e: any) => setQuestionType(e.target.value)}
                                        >

                                            <>
                                                <IonSelectOption>MCQ</IonSelectOption>
                                                <IonSelectOption>SQ</IonSelectOption>

                                            </>

                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Question</IonLabel>
                                        <IonInput type="text"
                                            name="title"
                                            value={question}
                                            onIonChange={(e: any) => setQuestion(e.target.value)}
                                            clearInput></IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked" >Content</IonLabel>
                                        <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                            <Editor
                                                textareaName="content"
                                                value={q_content}
                                                onEditorChange={(content: any, editor: any) => setQContent(content)}
                                                init={{
                                                    height: 500,
                                                    width: 1030,
                                                    menubar: true,
                                                    plugins: [

                                                        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                    ],

                                                    toolbar: 'undo redo | blocks | ' +
                                                        'bold italic forecolor | alignleft aligncenter ' +
                                                        'alignright alignjustify | bullist numlist outdent indent | ' +
                                                        'removeformat | help',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                                }}
                                            />
                                        </div>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Answer of this question</IonLabel>
                                        <IonInput type="text"
                                            name="answer"
                                            value={answer}
                                            disabled={true}

                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonText color="primary">
                                        <h2 className="mt-3 text-center">Add Question Option</h2>
                                    </IonText>


                                    <>
                                        <IonCard>
                                            <IonCardHeader>
                                                <IonCardTitle>
                                                    <button onClick={() => handleAdd()}>
                                                        <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                    </button>
                                                </IonCardTitle>

                                            </IonCardHeader>

                                            <IonCardContent>
                                                {option.map((op: any, i: any) => {
                                                    return (
                                                        <div key={i.toString()} className="mt-2">

                                                            <input className="mx-2"
                                                                type="radio"
                                                                name="answer"
                                                                value={op}
                                                                onChange={(e: any) => setAnswer(e.target.value)}
                                                            />


                                                            <input type="text"
                                                                name="option[]"
                                                                placeholder="options..."
                                                                value={op} onChange={e => handleChange(e, i)} />
                                                            <button onClick={() => handleDelete(i)}>x</button>
                                                        </div>
                                                    );
                                                })}
                                            </IonCardContent>


                                        </IonCard>
                                    </>
                                    <hr />
                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="danger" onClick={() => setOpen(false)}>
                                            Cancel
                                        </IonButton>
                                        <IonButton color="primary" onClick={updateQuestion}>
                                            Update
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </div>
                </Collapse>
                <MaterialReactTable
                    columns={columns}
                    data={quiz}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit" >
                                <IconButton aria-expanded={open} aria-controls="collapseID" onClick={() => {
                                    editQuestion(row.id)
                                    setOpen(!open)

                                }}>

                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteQuestion(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}

                />

            </div>
        </IonContent>

    );

};


export default Quiz;
