import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
import Question from '../../admin/quiz/Question';
const ViewQuiz: React.FC = () => {
    type ViewQuizParams = {
        id: string;
    };
    const [quizlevel, setQuizlevel] = useState([]);
    const [quiz_name, setQuiz_name] = useState('');
    const [level_id, setLevel_id] = useState('');
    const [total_question, setTotalQuestion] = useState('');
    const history = useHistory();
    const { id } = useParams<ViewQuizParams>();
    const back = () => {
        history.push("/dashboard/quiz")
    }

    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                // setQuiz_name(result.quiz.quiz_name);
                setQuiz_name(result.quiz_name);
                setQuizlevel(result.quizlevel);
                setLevel_id(result.quizlevel.map((l: any) => (l.id)));

            })
            .catch(function (error) {
                console.log(error);
            })


    }, [])
    useEffect(() => {

        fetch(`${baseUrl}${api.viewLevel}${level_id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {

                setTotalQuestion(result.question.length);


            })
            .catch(function (error) {
                console.log(error);
            })

    }, [])

    return (
        <>
            <IonContent>
                <IonToolbar>
                    <div className="text-center">
                        <IonTitle >
                            {quiz_name} Levels
                        </IonTitle>
                    </div>
                    <IonButtons slot="start">
                        <IonButton onClick={back} > <i className="bi bi-arrow-left"></i> </IonButton>
                    </IonButtons>
                </IonToolbar>
                {
                    quizlevel.map((item: any, i: any) => (
                        <IonItem key={i.toString()}>
                            <IonGrid>
                                <IonRow>
                                    <IonCol>

                                        {item.level_name}
                                    </IonCol>
                                    <IonCol>Per Question Mark: {item.mark}</IonCol>
                                    <IonCol>Total Time: {item.duration}</IonCol>
                                    <IonCol>Total Question: {total_question}</IonCol>

                                </IonRow>
                            </IonGrid>
                        </IonItem>
                    ))

                }

            </IonContent>

        </>

    );
}
export default ViewQuiz;
