import {
    IonContent,
    IonRouterOutlet,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import baseUrl, { api } from '../../pages/Urls';
import { Link, Route } from 'react-router-dom';
import TopicFrom from "./TopicFrom";

const TchrTopic: React.FC<{

    topic: any[]
    id: string
}> = (props) => {
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const deleteTopic = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteTopic}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        window.location.href = '/dashboard/topic';

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <>
            <IonContent>
                <IonRouterOutlet >
                    <Route exact path="/dashboard/mycourse/:id/topic/id">
                        <TopicFrom />
                    </Route>

                </IonRouterOutlet>
            </IonContent>
            <div className="container mt-5">

                <Paper sx={{ width: '100%', mb: 2 }}>
                    <p className="h1 text-center">List of Topic</p>
                    <TableContainer>

                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>No</TableCell>
                                    <TableCell >Topic Name</TableCell>
                                    <TableCell >Action</TableCell>

                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {(rowsPerPage > 0
                                    ? props.topic.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : props.topic
                                ).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell component="th" scope="row">{item.id}</TableCell>
                                        <TableCell >{item.topic_name}</TableCell>

                                        <TableCell >
                                            <Link className='text-decoration-none' to={`/dashboard/mycourse/${props.id}/topic/${item.id}`}>
                                                <i className="bi bi-pencil-square" title="edit"></i>
                                            </Link>
                                            {/* <button >
                                                <i className="bi bi-pencil-square" title="edit"></i>
                                            </button> */}

                                            <button onClick={() => deleteTopic(item.id)} >
                                                <i className="bi bi-trash" title="delete"></i>
                                            </button>
                                        </TableCell>
                                    </TableRow>
                                ))}


                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[5, 10, 20]}
                        component="div"
                        count={props.topic.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>

            </div>
        </>
    )
}
export default TchrTopic;
