import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    IconButton,
    Tooltip,
} from '@mui/material';
import { Input } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { IonContent } from '@ionic/react';
type CourseData = {
    id: string;
    course_name: string;
    description: string;

};

//nested data is ok, see accessorKeys in ColumnDef below


const CourseList: FC = () => {
    const [course, setCourse] = useState([]);
    const [courseData, setCourseData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);

    const fetchCourse = () => {
        fetch(`${baseUrl}${api.allCourse}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCourse(resp.reverse())
            })
    }
    useEffect(() => {
        fetchCourse();

    }, [])

    const columns = useMemo<MRT_ColumnDef<CourseData>[]>(
        () => [


            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'description',
                header: 'Description',

            },

        ],
        [],
    );

    const enroleCourse = (id: any) => {
        if (window.confirm('Want to enrole this course?')) {

            let data = {
                'course_id': id,
            }
            fetch(`${baseUrl}${api.courseEnrole}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),

            })

                .then(response => {
                    if (response.status === 200) {
                        alert('Enrole course Successfully');
                        fetchCourse();
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }

    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Courses</h1>
                <MaterialReactTable
                    columns={columns}
                    data={course}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions={(row) => row}
                    enableRowNumbers
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            {localStorage.getItem("role") == 'ar_instructor' ? '' :
                                <Tooltip arrow placement="right" title="Enrole-course">
                                    <IconButton style={{ color: "green" }} onClick={() => enroleCourse(row.id)}>
                                        <Input />
                                    </IconButton>
                                </Tooltip>
                            }
                        </Box>
                    )}

                />

            </div>
        </IonContent>

    );

};

export default CourseList;
