import {
    IonButton, IonButtons, IonHeader, IonTitle, IonToolbar, IonContent,
    IonIcon,
    IonItem,
    IonLabel,
    IonPopover,
    IonRouterLink,
} from '@ionic/react';
import {
    exitOutline,
    personCircle,
    settingsOutline,
} from "ionicons/icons";
import { Link } from 'react-router-dom';
import { useHistory } from "react-router";
const Nav: React.FC<{
    isAuth: boolean;
}> = (props) => {
    const history = useHistory();
    const logout = () => {
        localStorage.removeItem("token");

    };
    const loginPage = () => {

        history.push("/login");
    };

    return (

        <IonHeader>
            <IonToolbar>
                <IonTitle>E-learning</IonTitle>
                <IonButtons slot="secondary">
                    {!(props.isAuth) ? (
                        <>
                            <IonButton><Link to="/home" >Home</Link></IonButton>
                            <IonButton onClick={loginPage}>Login</IonButton>
                        </>
                    ) : (
                        <>
                            <IonButton id="profile-trigger">
                                <IonIcon slot="icon-only" icon={personCircle}></IonIcon>
                            </IonButton>
                            <IonPopover
                                trigger="profile-trigger"
                                triggerAction="click"
                                showBackdrop={false}
                            >
                                <IonContent>
                                    <IonItem>
                                        <IonIcon slot="end" icon={personCircle}></IonIcon>
                                        <IonLabel>username</IonLabel>
                                    </IonItem>
                                    <IonItem href="#">
                                        <IonIcon slot="end" icon={settingsOutline}></IonIcon>
                                        <IonLabel>Dashboard</IonLabel>
                                    </IonItem>
                                    <IonItem href="" onClick={logout} >
                                        <IonIcon slot="end" icon={exitOutline}></IonIcon>
                                        <IonLabel>Logout</IonLabel>
                                    </IonItem>
                                </IonContent>
                            </IonPopover>
                        </>
                    )}



                </IonButtons>
            </IonToolbar>
        </IonHeader>

    );
};

export default Nav;
