import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
    InputLabel,
    FormControl,
    Select
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Collapse } from 'react-bootstrap'
import AddLevel from './AddLevel';
import {


    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonPopover,
    IonDatetime,

} from "@ionic/react";
type levelData = {
    id: string;
    level_name: string;
    topic_name: string;


};

//nested data is ok, see accessorKeys in ColumnDef below


const TchrLevel: FC<{
    topic: any,
    level: any,
    course_name: string,
    fetchShowCourse: () => void
}> = (props) => {

    const [levelData, setLevelData]: any = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const [ltopic, setLTopic]: any[] = useState([]);
    const [level_name, setLevel_name] = useState('');
    const columns = useMemo<MRT_ColumnDef<levelData>[]>(
        () => [

            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },
            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },


        ],
        [],
    );

    const deleteLevel = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteLevel}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        props.fetchShowCourse();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }


    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>
            <UpdateLevel
                collapse={collapse}
                onClose={() => setCollapse(false)}
                fetchShowCourse={() => props.fetchShowCourse()}
                levelData={levelData}
                topic={props.topic}
            />
            <AddLevel
                open={open}
                onClose={() => setOpen(false)}
                fetchShowCourse={() => props.fetchShowCourse()}
                topic={props.topic}
                course_name={props.course_name}

            />
            <Collapse in={collapse}>
                <div id="collapseID">
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Level</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Level Name</IonLabel>
                                    <IonInput type="text"
                                        name="level"
                                        value={level_name}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setLevel_name(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Topic</IonLabel>
                                    <IonSelect
                                        name="topic"
                                        value={ltopic}
                                        onIonChange={(e: any) => setLTopic(e.target.value)}

                                        placeholder="Select topic" multiple={true}>
                                        {props.topic.map((item: any, i: any) => (
                                            < IonSelectOption key={i.toString()}>{item.topic_name}</IonSelectOption>
                                        ))}
                                    </IonSelect>

                                </IonItem>


                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="danger" onClick={() => setCollapse(false)}>
                                        Cancel
                                    </IonButton>
                                    <IonButton color="primary">
                                        Save
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </div>
            </Collapse>
            <h1 className='text-center mb-2'>List of Levels</h1>
            <MaterialReactTable
                columns={columns}
                data={props.level}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                enableRowNumbers
                enableColumnOrdering
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="Edit">
                            <IconButton onClick={() => {
                                setLevelData(row.original)
                                setCollapse(!collapse);
                            }}>
                                <Edit />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteLevel(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add-Course">
                        <IconButton aria-expanded={open} onClick={() => setOpen(!open)}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}
            />

        </div>


    );

};

const UpdateLevel: FC<{
    onClose: () => void;
    fetchShowCourse: () => void;
    collapse: boolean;
    levelData: any
    topic: any
}> = (props) => {
    const [id, setId] = useState('');
    const [ltopic, setLTopic]: any[] = useState([]);
    const [level_name, setLevel_name] = useState('');
    const [level_topic, setLevel_topic] = useState([]);
    useEffect(() => {
        setLevel_name(props.levelData.level_name)
        setLevel_topic(props.levelData.ltopic)
        //setLTopic(props.levelData.ltopic.map((lt: any) => (lt.topic_name)))
        setId(props.levelData.id);

    }, [props.levelData]);
    useEffect(() => {

        setLTopic(level_topic.map((lt: any) => (lt.topic_name)))


    }, [level_topic]);

    return (
        <Collapse in={props.collapse}>
            <div id="collapseID">
                <div className="container mt-5 mb-5 ">

                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <IonToolbar>
                                    <IonTitle><h1 className="text-center display-6">Edit Level</h1></IonTitle>
                                </IonToolbar>
                            </IonCardTitle>
                        </IonCardHeader>
                        <hr />
                        <IonCardContent>
                            <IonItem>
                                <IonLabel position="stacked">Level Name</IonLabel>
                                <IonInput type="text"
                                    name="level"
                                    value={level_name}
                                    required
                                    clearInput
                                    onIonChange={(e: any) => setLevel_name(e.target.value)}
                                >
                                </IonInput>
                            </IonItem>
                            <IonItem>
                                <IonLabel position="stacked">Topic</IonLabel>
                                <IonSelect
                                    name="topic"
                                    value={ltopic}
                                    onIonChange={(e: any) => setLTopic(e.target.value)}

                                    placeholder="Select topic" multiple={true}>
                                    {props.topic.map((item: any, i: any) => (
                                        < IonSelectOption key={i.toString()}>{item.topic_name}</IonSelectOption>
                                    ))}
                                </IonSelect>

                            </IonItem>


                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="danger" onClick={props.onClose}>
                                    Cancel
                                </IonButton>
                                <IonButton color="primary">
                                    Save
                                </IonButton>

                            </div>
                        </IonCardContent>
                    </IonCard>

                </div>

            </div>
        </Collapse>
    );
}
export default TchrLevel;
