import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonText,

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect } from 'react';
import MaterialReactTable, {
    MRT_ColumnDef,

} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Stack,
    Tooltip,
    FormControl,
    NativeSelect,
} from '@mui/material';
import { Delete, Edit, Add, Visibility } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import Select from 'react-select';
import swal from 'sweetalert';
import { useForm, Controller, useFieldArray } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
type questionData = {
    id: string;
    title: string;
    question_type: string;
    answer: string;
    topic_name: string;
    level_name: string;

};


const TchrQuestion: FC<{
    level: any,
    question: any
    course_name: string
    fetchShowCourse: () => void
}> = (props) => {

    const [error, setError] = useState("");
    const [collapse, setCollapse] = useState(false);
    const [modalOpen, setModalOpen] = useState(false);
    const [questionData, setQuestionData] = useState({});
    const [option, setOption] = useState([]);
    const [qType, setQType] = useState('');
    const [topic, setTopic] = useState([]);
    const [levelOptions, setLevelOptions] = useState([{}]);
    const [qstnId, setQstnId] = useState('');
    const type = qstnId ? "E" : "C";
    const {
        handleSubmit,
        register,
        formState: { errors },
        reset,
        control,
        setValue,
        watch,
        getValues,
        clearErrors
    } = useForm({
        defaultValues: {
            id: '',
            level_id: {},
            topic_name: '',
            title: '',
            q_content: '',
            question_type: '',
            answer: '',
            option: [{ data: '' }, { data: '' }]


        }

    });
    const { fields, remove, append } = useFieldArray({
        name: 'option',
        control,
    });
    const columns = useMemo<MRT_ColumnDef<questionData>[]>(
        () => [


            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },
            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },
            {
                accessorKey: 'title',
                header: 'Question',

            },

            {
                accessorKey: 'question_type',
                header: 'Question Type',

            },

            {
                accessorKey: 'answer',
                header: 'Question Answer',

            },

        ],
        [],
    );
    useEffect(() => {
        setLevelOptions(props.level.map((item: any) => ({
            label: item.level_name,
            value: item.id,

        })))
    }, [props.level])

    const handleSelectChange = (e: any) => {

        const lid = e.value;

        if (lid) {
            fetch(`${baseUrl}${api.allLevelTopic}${lid}`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }
            })
                .then(resp => resp.json())
                .then(resp => {
                    setTopic(resp.map((item: any) => ({
                        label: item.topic_name,
                        value: item.topic_name
                    })))
                })
        }

    }

    const addQuestion = (e: any) => {

        {

            fetch(`${baseUrl}${api.addQuestion}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchShowCourse();
                        setCollapse(false)
                        swal({
                            title: "Success!",
                            text: "Question Created successfully",
                            icon: "success",
                        });

                    } else if (response.status === 406) {
                        setError('At least two option is required')
                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    /////////////






    const editQuestion = (id: any) => {
        setQstnId(id);
        fetch(`${baseUrl}${api.showQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setValue('title', result.qstn.title);
                setValue('q_content', result.qstn.q_content);
                setValue('question_type', result.qstn.question_type);
                setValue('answer', result.qstn.answer);
                setValue('id', result.qstn.id);
                setValue('option',
                    result.qstn.option.map((item: any, i: any) => ({
                        option: item.option,
                    }))
                );
                setQType(result.qstn.question_type)
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateQuestion = (e: any) => {

        fetch(`${baseUrl}${api.updateQuestion}${e.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(e),
        })

            .then(res => {
                if (res.status === 200) {
                    props.fetchShowCourse();
                    setCollapse(false)
                    swal({
                        title: "Success!",
                        text: "Question updated successfully",
                        icon: "success",
                    });
                } else {
                    swal({
                        title: "Error!",
                        text: res.status + ' ' + res.statusText +
                            " - Question not updated",
                        icon: "error",
                    });
                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteQuestion = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuestion}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })
                .then(res => {
                    if (res.status === 200) {

                        props.fetchShowCourse();
                        swal({
                            title: "Success!",
                            text: "Question deleted successfully",
                            icon: "success",
                        });
                    } else {
                        swal({
                            title: "Error!",
                            text: res.status + ' ' + res.statusText +
                                " - Question not deleted",
                            icon: "error",
                        });
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    const onSubmit = (e: any) => {
        type === "C" ? addQuestion(e) : updateQuestion(e);
    }
    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>
            <Collapse in={collapse}>
                <div className="container mt-5 mb-5 ">

                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <IonToolbar>
                                    <IonTitle><h1 className="text-center display-6">{type === "C" ? "Create" : "Edit"} Question</h1></IonTitle>
                                </IonToolbar>
                            </IonCardTitle>
                        </IonCardHeader>
                        <hr />
                        <IonCardContent>
                            <form onSubmit={handleSubmit(onSubmit)}>
                                {type === 'C' ?
                                    <>
                                        <IonItem>
                                            <IonLabel position="stacked">Level</IonLabel>
                                            <div className="mt-2" style={{ width: '100%' }}>

                                                <Controller
                                                    control={control}
                                                    name="level_id"
                                                    rules={{
                                                        required: 'Level name is required',
                                                    }}

                                                    render={() => (
                                                        <Select

                                                            menuPortalTarget={document.body}
                                                            options={levelOptions}
                                                            placeholder={<i className="bi bi-search"> Select Level</i>}
                                                            onChange={(e: any) => {
                                                                handleSelectChange(e)
                                                                setValue('level_id', e.value)
                                                            }}

                                                        />

                                                    )}

                                                />


                                            </div>
                                            {/* <IonSelect

                                    onIonChange={(e: any) => handleSelectChange(e)}
                                    {...register("level_id", { required: "Level Name Is Required" })}
                                    placeholder="Select level" >
                                    {props.level.map((item: any, i: any) => (
                                        < IonSelectOption key={i.toString()} value={item.id}>{item.level_name}</IonSelectOption>
                                    ))}
                                </IonSelect> */}

                                        </IonItem>
                                        <p style={{ color: "red" }}>
                                            <ErrorMessage errors={errors} name='level_id' />
                                        </p>
                                        <IonItem>
                                            <IonLabel position="stacked">Topic</IonLabel>
                                            <div className="mt-2" style={{ width: '100%' }}>

                                                <Controller
                                                    control={control}
                                                    name="topic_name"
                                                    rules={{
                                                        required: 'Topic name is required',
                                                    }}
                                                    render={() => (
                                                        <Select

                                                            menuPortalTarget={document.body}
                                                            options={topic}
                                                            placeholder={<i className="bi bi-search"> Select Topic</i>}
                                                            onChange={(e: any) => setValue('topic_name', e)}

                                                        />

                                                    )}

                                                />
                                            </div>

                                        </IonItem>
                                        <p style={{ color: "red" }}>
                                            <ErrorMessage errors={errors} name='topic_name' />
                                        </p>
                                        <IonItem>
                                            <IonLabel position="stacked">Question Type</IonLabel>
                                            <Box sx={{ width: '100%' }}>
                                                <FormControl fullWidth sx={{ mt: 1 }}>
                                                    <NativeSelect
                                                        {...register("question_type", { required: "Question type Is Required" })}
                                                        onChange={(e: any) => setQType(e.target.value)}
                                                    >
                                                        <option value='MCQ'>MCQ</option>
                                                        <option value='SQ'>Short Question</option>


                                                    </NativeSelect>
                                                </FormControl>
                                            </Box>
                                            {/* <IonSelect
                                    onIonChange={(e: any) => setQType(e.target.value)}
                                    placeholder="Select question_type"
                                    {...register("question_type", { required: "Question type Is Required" })}
                                >

                                    <>
                                        <IonSelectOption value='MCQ'>MCQ</IonSelectOption>
                                        <IonSelectOption value='SQ'>Short Question</IonSelectOption>

                                    </>

                                </IonSelect> */}

                                        </IonItem>
                                        <p style={{ color: "red" }}>
                                            <ErrorMessage errors={errors} name='question_type' />
                                        </p>
                                    </>
                                    : ''

                                }
                                <IonItem>
                                    <IonLabel position="stacked">Question</IonLabel>
                                    <IonInput type="text"

                                        {...register("title", { required: "Question Title Is Required" })}></IonInput>
                                </IonItem>
                                <p style={{ color: "red" }}>
                                    <ErrorMessage errors={errors} name='title' />
                                </p>
                                <IonItem>
                                    <IonLabel position="stacked" >Content</IonLabel>
                                    <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                        <Controller
                                            name="q_content"
                                            control={control}

                                            render={({ field: { ...rest } }) => (
                                                <Editor
                                                    textareaName="q_content"
                                                    value={rest.value}

                                                    init={{
                                                        height: 500,
                                                        width: 1030,
                                                        menubar: true,
                                                        plugins: [

                                                            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                        ],

                                                        toolbar: 'undo redo | blocks | ' +
                                                            'bold italic forecolor | alignleft aligncenter ' +
                                                            'alignright alignjustify | bullist numlist outdent indent | ' +
                                                            'removeformat | help',
                                                        content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'

                                                    }}
                                                    onEditorChange={(e) => {
                                                        setValue('q_content', e)
                                                    }}
                                                />

                                            )}
                                        />
                                    </div>

                                </IonItem>
                                {qType === 'MCQ' ?
                                    <>
                                        <IonItem>
                                            <IonText color="primary">
                                                <h2 className="mt-3 mb-3">Add Question Option</h2>
                                            </IonText>

                                        </IonItem>
                                        <IonItem>
                                            <input type='hidden'
                                                {...register('answer', { required: "Select one Answer" })}
                                            >
                                            </input>

                                            <div>

                                                {

                                                    fields.map((x, i) => {

                                                        return (
                                                            <div key={x.id} className="row mb-3 mt-3">
                                                                <div className=" col-md-1">

                                                                    <input className="mx-2 mt-4"
                                                                        type="radio"
                                                                        name="answer"
                                                                        value={`option.${i}.data`}
                                                                        checked={`option.${i}.data` === getValues('answer')}
                                                                        onChange={(e: any) => {
                                                                            setValue('answer', watch(`option.${i}.data`))
                                                                        }}
                                                                    />

                                                                </div>
                                                                <div className=" col-md-6">

                                                                    <IonItem>
                                                                        <IonLabel position="stacked">Option</IonLabel>
                                                                        <IonInput type="text"

                                                                            {...register(`option.${i}.data` as const, { required: "Option Is Required" })}
                                                                            value={getValues(`option.${i}.data`)}
                                                                        >
                                                                        </IonInput>
                                                                    </IonItem>

                                                                    <p style={{ color: "red" }}>
                                                                        <ErrorMessage errors={errors} name={`option.${i}.data`} />
                                                                    </p>

                                                                </div>
                                                                <div className="col-md-3 mt-4">
                                                                    {
                                                                        fields.length !== 1 &&
                                                                        <button onClick={() => remove(i)}>X</button>
                                                                    }
                                                                    {
                                                                        fields.length - 1 == i &&
                                                                        <button onClick={() => append({ data: '' })}>
                                                                            <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                                        </button>
                                                                    }


                                                                </div>



                                                            </div>
                                                        );
                                                    })}

                                            </div>

                                        </IonItem>
                                        <p style={{ color: "red" }}>
                                            <ErrorMessage errors={errors} name='answer' />
                                            <br />
                                            {error ? error : ''}
                                        </p>

                                    </>
                                    :
                                    <>
                                        <IonItem>
                                            <IonLabel position="stacked">Answer</IonLabel>
                                            <IonInput type="text"

                                                {...register('answer', { required: "Answer Is Required" })}></IonInput>
                                        </IonItem>
                                        <p style={{ color: "red" }}>
                                            <ErrorMessage errors={errors} name='answer' />
                                        </p>
                                    </>
                                }
                                <hr />
                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="danger" onClick={() => setCollapse(false)}>
                                        Cancel
                                    </IonButton>
                                    <IonButton color="primary" type="submit">
                                        {type === "C" ? "Create" : "Save"}
                                    </IonButton>

                                </div>
                            </form>
                        </IonCardContent>
                    </IonCard>

                </div>

            </Collapse >

            <h1 className='text-center mb-2'>List of Questions</h1>
            <MaterialReactTable
                columns={columns}
                data={props.question}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                enableRowNumbers
                enableColumnOrdering
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="Edit" >
                            <IconButton aria-expanded={collapse} aria-controls="collapseID" onClick={() => {
                                editQuestion(row.id)
                                setCollapse(!collapse)
                                clearErrors([
                                    'title',
                                    'q_content',
                                    'answer',
                                    'option',

                                ])
                            }}>

                                <Edit />
                            </IconButton>
                        </Tooltip>

                        <Tooltip arrow placement="left" title="View" >
                            <IconButton onClick={() => {
                                editQuestion(row.id)
                                setModalOpen(true);
                                setQuestionData(row.original)
                            }}>

                                <Visibility />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteQuestion(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add">
                        <IconButton aria-expanded={collapse} onClick={() => {
                            setCollapse(!collapse)
                            setError('')
                            setQstnId('')
                            setQType('')
                            setTopic([])
                            reset({
                                id: '',
                                level_id: { label: '' },
                                topic_name: '',
                                title: '',
                                q_content: '',
                                question_type: '',
                                answer: '',
                                option: [{ data: '' }, { data: '' }],
                            })
                        }}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}

            />
            <ViewQuestion
                open={modalOpen}
                onClose={() => {
                    setModalOpen(false)

                }}
                option={option}
                questionData={questionData}
            />
        </div>


    );

};


