    IonCardHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonCard,
    IonIcon,

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router";
const ViewBlog: React.FC = () => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');
    const history = useHistory();
    const back = () => {
        history.push("/dashboard/blog")
    }
    return (
        <>

            <IonCard>

                <IonCardHeader>
                    <IonToolbar>
                        <div className="text-center">
                            <IonTitle >title
                            </IonTitle>
                        </div>
                        <IonButtons slot="start">
                            <IonButton onClick={back} > </IonButton>
                            <IonIcon name="arrow-back-outline"></IonIcon>
                        </IonButtons>
                    </IonToolbar>
                </IonCardHeader>


                <IonCardContent>

                    <h1 color="dark">title</h1>


                    <h2>Content</h2>

                    <p>date</p>


                </IonCardContent>



            </IonCard>

        </>

    );
};
export default ViewBlog;
