import {
    IonContent,
    IonCard, IonCardContent, IonCardHeader, IonCardTitle,
    IonCol, IonGrid, IonRow,
    IonItem, IonButton, IonNote,
    IonRouterOutlet,

} from '@ionic/react';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import TextField from '@mui/material/TextField';
import SearchIcon from '@mui/icons-material/Search';
import '../../../pages/Style.css';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { Route, useHistory } from "react-router";
import { Link } from 'react-router-dom';
import { format } from 'date-fns'
import ReactPaginate from "react-paginate";
import ViewBlog from './ViewBlog';
const Blog: React.FC = () => {

    const [article, setArticle] = useState([]);
    const [allCategory, setAllCategory] = useState([]);
    const history = useHistory();

    const fetchAuthBlog = () => {
        fetch(`${baseUrl}${api.authBlog}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }
    const fetchCategory = () => {
        fetch(`${baseUrl}${api.allCategory}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setAllCategory(resp)
            })
    }


    useEffect(() => {
        fetchAuthBlog();
        fetchCategory();
    }, [])

    const search = (text: any) => {
        if (text) {
            let matches = article.filter((item: any) => {
                const regex = new RegExp(`${text}`, "gi");
                return item.title.match(regex);
            })
            setArticle(matches);
        } else {
            fetchAuthBlog();
        }

    };

    return (
        <IonContent>
            <IonRouterOutlet id="main-content">
                <Route path="/dashboard/blog" exact>
                    <BlogIndex

                        allCategory={allCategory}
                        article={article}
                        fetchAuthBlog={() => fetchAuthBlog()}
                        search={(text: any) => search(text)}
                    />
                </Route>
                <Route path="/dashboard/blog/:date/:date/:date/:slug" exact>
                    <ViewBlog />
                </Route>
            </IonRouterOutlet>

        </IonContent>

    );
}

const BlogIndex: React.FC<{
    search: (text: any) => void;
    fetchAuthBlog: () => void;

    allCategory: any;
    article: any;


}> = (props) => {
    const [pageNumber, setPageNumber] = useState(0);
    const articlePerPage = 10;
    const pagesVisited = pageNumber * articlePerPage;
    const pageCount = Math.ceil(props.article.length / articlePerPage);
    const changePage = ({ selected }: any) => {
        setPageNumber(selected);
    };
    return (
        <IonContent>
            <IonGrid>
                <IonRow>
                    <IonCol>
                        <div className="container mt-3 mb-5">
                            <h2 >Recent Articles Post</h2>
                            <hr />
                            <Box sx={{ display: 'flex', justifyContent: "flex-end", mx: 1 }}>
                                <TextField
                                    id="input-with-icon-textfield"
                                    InputProps={{
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <SearchIcon />
                                            </InputAdornment>
                                        ),
                                    }}
                                    variant="standard"
                                    onChange={(e: any) => props.search(e.target.value)}
                                />
                            </Box>
                            {
                                props.article.length != 0 ?
                                    <Box sx={{ width: '100%', mb: 2, mt: 3 }}>


                                        {(articlePerPage > 0
                                            ? props.article.slice(pagesVisited, pagesVisited + articlePerPage)
                                            : props.article).map((item: any, i: any) => (
                                                <IonCard key={i.toString()}>
                                                    <IonCardHeader>
                                                        <IonCardTitle>
                                                            <h4 className="text-primary">
                                                                <Link className='text-decoration-none'
                                                                    to={`/dashboard/blog/${format(new Date(item.date), 'yyyy')}/${format(new Date(item.date), 'MM')}/${item.slug}`}>
                                                                    {item.title}
                                                                </Link></h4>
                                                        </IonCardTitle>

                                                    </IonCardHeader>

                                                    <IonCardContent>
                                                        {format(new Date(item.date), 'yyyy-MM-dd')}
                                                    </IonCardContent>
                                                </IonCard>

                                            ))}

                                    </Box>
                                    :
                                    <div className='text-center mt-5'>
                                        <IonNote color="danger">**Not match search data**</IonNote>
                                    </div>

                            }


                        </div>
                        {props.article.length != 0 && props.article.length > articlePerPage ?
                            <ReactPaginate
                                nextLabel=" >"
                                onPageChange={changePage}
                                pageRangeDisplayed={3}
                                marginPagesDisplayed={2}
                                pageCount={pageCount}
                                previousLabel="< "
                                pageClassName="page-item"
                                pageLinkClassName="page-link"
                                previousClassName="page-item"
                                previousLinkClassName="page-link"
                                nextClassName="page-item"
                                nextLinkClassName="page-link"
                                breakLabel="..."
                                breakClassName="page-item"
                                breakLinkClassName="page-link"
                                containerClassName="pagination"
                                activeClassName="active"
                                renderOnZeroPageCount={null || undefined}
                            /> : ''}
                    </IonCol>

                    <IonCol size="auto">

                        <div style={{ width: "200px" }}>
                            <h3>Categories</h3>

                            <IonItem >
                                <IonButton fill="clear" onClick={() => props.fetchAuthBlog()}>All Articles</IonButton>
                            </IonItem>
                            {props.allCategory.map((cat: any, i: any) =>
                                <IonItem key={i.toString()}>
                                    <IonButton fill="clear">
                                        <Link className='text-decoration-none' to={`/dashboard/blog/category/${cat.slug}`}>
                                            {cat.name}
                                        </Link>
                                    </IonButton>
                                </IonItem>
                            )}


                        </div>

                    </IonCol>

                </IonRow>
            </IonGrid>

        </IonContent>

    );
}
export default Blog;
