import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
import { Typography } from '@mui/material';
type CourseData = {
    id: string;
    course_name: string;
    description: string;

};

//nested data is ok, see accessorKeys in ColumnDef below


const Course: FC = () => {
    const [course, setCourse] = useState([]);
    const [courseData, setCourseData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const fetchCourse = () => {
        fetch(`${baseUrl}${api.allCourse}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCourse(resp.reverse())
            })
    }
    useEffect(() => {
        fetchCourse();
    }, [])

    const columns = useMemo<MRT_ColumnDef<CourseData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
            },
            {
                accessorKey: 'name',
                header: 'Course Name',

            },
            {
                accessorKey: 'name',
                header: 'Description',

            },

        ],
        [],
    );

    const deleteCourse = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteCourse}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        fetchCourse();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Courses</h1>
                <MaterialReactTable
                    columns={columns}
                    data={course}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    setCourseData(row.original)
                                    setModalOpen(true);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteCourse(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-group">
                            <IconButton onClick={() => setModalOpen(true)}>
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />
                <CreateOrEdit
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)
                        setCourseData({})

                    }}
                    fetchCourse={() => fetchCourse()}
                    courseData={courseData}

                />
            </div>
        </IonContent>

    );

};

const CreateOrEdit: FC<{
    onClose: () => void;
    fetchCourse: () => void;
    open: boolean;
    courseData: any
}> = (props) => {
    console.log(props.courseData)
    const type = Object.keys(props.courseData).length == 0 ? "C" : "E";
    const formRef = useRef<HTMLFormElement>(null);
    const {
        register,
        watch,
        reset,
        handleSubmit,
        setValue,
        formState: { errors },
    } = useForm({
        criteriaMode: "all",

    });
    const addCourse = (e: any) => {
        {


            // fetch(`${baseUrl}${api.addCourse}`, {
            //     method: 'POST',
            //     headers: {
            //         Accept: 'application/json',
            //         'Content-Type': 'application/json',
            //         'Authorization': `Bearer ${localStorage.getItem("token")}`
            //     },
            //     body: JSON.stringify(e),
            // })

            //     .then(response => {
            //         if (response.status === 200) {
            //             window.alert('Create Group Successfully');
            //             props.fetchGroup();
            //             props.onClose();
            //         }
            //         else {
            //             throw Error([response.status, response.statusText].join(' '));
            //         }
            //     })

            //     .catch(error => console.log('error', error));

        }
    }
    const updateCourse = (e: any) => {

        fetch(`${baseUrl}${api.updateCourse}${e.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(e),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Group Successfully');
                    props.fetchCourse();
                    props.onClose();

                }

            })
            .catch(error => console.log('error', error));


    }
    const onSubmit = (e: any) => {
        type === "C" ? addCourse(e) : updateCourse(e);
    }
    useEffect(() => {

        type === "E" &&
            reset({
                id: props.courseData.id,
                name: props.courseData.name,

            });
    }, [props.courseData]);

    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">{type === "C" ? "Create" : "Edit"} Group</DialogTitle>
            <DialogContent>
                <form ref={formRef} onSubmit={handleSubmit(onSubmit)}>
                    <Stack
                        sx={{
                            width: '100%',
                            minWidth: { xs: '300px', sm: '360px', md: '400px' },
                            gap: '1.5rem',
                        }}
                    >

                        <TextField sx={{ mt: 3 }}
                            type="text"
                            label="Course Name"
                            variant="filled"
                            focused
                            {...register("name")}
                        />
                        <TextField sx={{ mt: 3 }}
                            type="text-area"
                            label="DEscription"
                            variant="filled"
                            focused
                            {...register("name")}
                        />

                    </Stack>
                </form>
            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={addCourse}>
                    {type === "C" ? "Create" : "Save"}
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default Course;
