import {
    IonContent,
    IonItem,
    IonLabel,
    IonCard,
    IonAccordion,
    IonAccordionGroup,
    IonText,

} from '@ionic/react';
import { format } from 'date-fns'
import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import baseUrl, { api } from '../../Urls';
const ViewUserCourse: React.FC<{
    quiz: any[],
    topic: any[],
    course_name: string,
    courseId: string,


}> = (props) => {

    const history = useHistory();
    const current_date = format(new Date(), 'yyyy-MM-dd');

    const downloadFile = (id: any) => {
        fetch(`${baseUrl}${api.downloadTopicContent}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            },

        }).then(response => {
            response.blob().then(blob => {
                let url = window.URL.createObjectURL(blob);
                let a = document.createElement('a');
                a.href = url;
                a.download = 'employees.json';
                a.click();
            });
            //window.location.href = response.url;
        });
    }

    return (
        <>
            <IonContent fullscreen={true}>

                <IonText color="primary">
                    <h1 className='mx-2'>{props.course_name}</h1>
                </IonText>

                <>

                    <IonCard >

                        <IonAccordionGroup>
                            <IonAccordion value="first">
                                <IonItem slot="header" color="light">
                                    <IonLabel>Quiz</IonLabel>
                                </IonItem>

                                <div className="ion-padding" slot="content">
                                    {props.quiz.map((item: any, i: any) =>

                                        <IonItem key={i.toString()}>
                                            <IonLabel>
                                                <Link className='text-decoration-none'

                                                    to={item.date == current_date && item.ahistory.map((ah: any) => (ah.is_attempt)) != 1 ?
                                                        `/dashboard/mycourse/${props.courseId}/quiz/${item.id}` : `/dashboard/mycourse/${props.courseId}`} >
                                                    {item.quiz_name}

                                                </Link>

                                            </IonLabel>
                                            {item.date == current_date ?
                                                <p className='text-info'>
                                                    {item.ahistory.map((ah: any) => (ah.is_attempt)) == 1 ? "You are already attempted" : "New"}
                                                </p> : <p className='text-danger'>Date expired</p>
                                            }

                                        </IonItem>
                                    )}
                                </div>
                            </IonAccordion>
                        </IonAccordionGroup>

                    </IonCard>
                    {props.topic.map((item: any, i: any) =>
                        <IonCard key={i.toString()}>

                            <IonAccordionGroup>
                                <IonAccordion value="first">
                                    <IonItem slot="header" color="light">
                                        <IonLabel>{item.topic_name}</IonLabel>
                                    </IonItem>

                                    <div className="ion-padding" slot="content">
                                        {item.topic_article.length == 0 && item.topic_content.length == 0 ? 'No data preview' :
                                            <>
                                                {item.topic_article.map((ta: any, i: any) =>
                                                    <IonItem key={i.toString()}>
                                                        <Link className='text-decoration-none'
                                                            to={{
                                                                pathname: `/dashboard/mycourse/${item.course_id}/article/${ta.id}`,
                                                                state: {
                                                                    artname: ta.name,
                                                                    artcontent: ta.content,
                                                                    course_id: item.course_id,

                                                                }
                                                            }} >
                                                            {ta.name}</Link>

                                                    </IonItem>



                                                )}
                                                {item.topic_content.map((tc: any, i: any) =>
                                                    <IonItem key={i.toString()}>
                                                        <IonLabel > <button onClick={() => downloadFile(tc.id)}>
                                                            {tc.file} <i className="bi bi-download"></i>
                                                        </button></IonLabel>
                                                    </IonItem>

                                                )}
                                            </>

                                        }
                                    </div>
                                </IonAccordion>
                            </IonAccordionGroup>

                        </IonCard>
                    )}
                </>

            </ IonContent>

        </>
    );


}




export default ViewUserCourse;
