import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonCard,

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
const ViewBlog: React.FC = () => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');

    return (
        <>

            <IonCard>

                <IonHeader>
                    <IonToolbar>
                        <div className="text-center">
                            <IonTitle >{title}
                            </IonTitle>
                        </div>
                        <IonButtons slot="start">
                            <IonButton  > <i className="bi bi-backspace"> Back</i> </IonButton>
                        </IonButtons>
                    </IonToolbar>
                </IonHeader>

                <IonContent>
                    <IonCardContent>
                        <IonItem >
                            <h1 color="dark">{title}</h1>

                        </IonItem>
                        <IonItem>
                            <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>
                        </IonItem>
                        <IonItem>
                            <p>{date}</p>

                        </IonItem>
                    </IonCardContent>

                </IonContent>

            </IonCard>

        </>

    );
};
export default ViewBlog;
