    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import MaterialReactTable, {
    MRT_ColumnDef,

} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Stack,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Visibility } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
type questionData = {
    id: string;
    course_name: string;
    topic_name: string;
    level_name: string;
    title: string;
    question_type: string;
    answer: string;

};



const Question: FC = () => {
    const [questions, setQuestions] = useState([]);
    const [question, setQuestion] = useState('');
    const [q_content, setQContent] = useState('');
    const [questionType, setQuestionType] = useState('');
    const [answer, setAnswer] = useState('');
    const [option, setOption]: any[] = useState([]);
    const [id, setId] = useState('');
    const [error, setError] = useState("");
    const [open, setOpen] = useState(false);
    const [questionData, setQuestionData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const fetchQuestions = () => {
        fetch(`${baseUrl}${api.allQuestion}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setQuestions(resp.reverse())
            })
    }
    useEffect(() => {
        fetchQuestions();
    }, [])

    const columns = useMemo<MRT_ColumnDef<questionData>[]>(
        () => [


            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },
            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },
            {
                accessorKey: 'title',
                header: 'Question',

            },

            {
                accessorKey: 'question_type',
                header: 'Question Type',

            },

            {
                accessorKey: 'answer',
                header: 'Question Answer',

            },

        ],
        [],
    );


    const handleAdd = () => {
        const abc = [...option, []]
        setOption(abc)
    }
    const handleChange = (onChangeValue: any, i: any) => {
        const inputdata = [...option]
        inputdata[i] = onChangeValue.target.value;
        setOption(inputdata)
    }
    const handleDelete = (i: any) => {
        const deletVal = [...option]
        deletVal.splice(i, 1)
        setOption(deletVal)
    }
    const editQuestion = (id: any) => {

        fetch(`${baseUrl}${api.showQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setQuestion(result.qstn.title);
                setQContent(result.qstn.q_content);
                setQuestionType(result.qstn.question_type);
                setAnswer(result.qstn.answer);
                setOption(result.qstn.option.map((op: any) => (op.option)));


                setId(result.qstn.id);
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateQuestion = () => {
        let data = {
            'title': question,
            'q_content': q_content,
            'question_type': questionType,
            'option': option,
            'answer': answer,
        }
        fetch(`${baseUrl}${api.updateQuestion}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    fetchQuestions();
                    setOpen(false)

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteQuestion = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuestion}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchQuestions();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }


    return (
        <IonContent>
            <div className="container mt-5">

                <Collapse in={open}>
                    <div id="collapseID">
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Question</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Question Type</IonLabel>

                                        <IonSelect
                                            name="question_type"
                                            value={questionType}
                                            onIonChange={(e: any) => setQuestionType(e.target.value)}
                                        >

                                            <>
                                                <IonSelectOption>MCQ</IonSelectOption>
                                                <IonSelectOption>SQ</IonSelectOption>

                                            </>

                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Question</IonLabel>
                                        <IonInput type="text"
                                            name="title"
                                            value={question}
                                            onIonChange={(e: any) => setQuestion(e.target.value)}
                                            clearInput></IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked" >Content</IonLabel>
                                        <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                            <Editor
                                                textareaName="content"
                                                value={q_content}
                                                onEditorChange={(content: any, editor: any) => setQContent(content)}
                                                init={{
                                                    height: 500,
                                                    width: 1030,
                                                    menubar: true,
                                                    plugins: [

                                                        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                    ],

                                                    toolbar: 'undo redo | blocks | ' +
                                                        'bold italic forecolor | alignleft aligncenter ' +
                                                        'alignright alignjustify | bullist numlist outdent indent | ' +
                                                        'removeformat | help',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                                }}
                                            />
                                        </div>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Answer of this question</IonLabel>
                                        <IonInput type="text"
                                            name="answer"
                                            value={answer}
                                            disabled={true}

                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonText color="primary">
                                        <h2 className="mt-3 text-center">Add Question Option</h2>
                                    </IonText>


                                    <>
                                        <IonCard>
                                            <IonCardHeader>
                                                <IonCardTitle>
                                                    <button onClick={() => handleAdd()}>
                                                        <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                    </button>
                                                </IonCardTitle>

                                            </IonCardHeader>

                                            <IonCardContent>
                                                {option.map((op: any, i: any) => {
                                                    return (
                                                        <div key={i.toString()} className="mt-2">

                                                            <input className="mx-2"
                                                                type="radio"
                                                                name="answer"
                                                                value={op}
                                                                onChange={(e: any) => setAnswer(e.target.value)}
                                                            />


                                                            <input type="text"
                                                                name="option[]"
                                                                placeholder="options..."
                                                                value={op} onChange={e => handleChange(e, i)} />
                                                            <button onClick={() => handleDelete(i)}>x</button>
                                                        </div>
                                                    );
                                                })}
                                            </IonCardContent>


                                        </IonCard>
                                    </>
                                    <hr />
                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="danger" onClick={() => setOpen(false)}>
                                            Cancel
                                        </IonButton>
                                        <IonButton color="primary" onClick={updateQuestion}>
                                            Save
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </div>
                </Collapse>
                <h1 className='text-center mb-2'>List of Questions</h1>
                <MaterialReactTable
                    columns={columns}
                    data={questions}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableRowNumbers
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit" >
                                <IconButton aria-expanded={open} aria-controls="collapseID" onClick={() => {
                                    editQuestion(row.id)
                                    setOpen(!open)

                                }}>

                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="left" title="View" >
                                <IconButton onClick={() => {
                                    editQuestion(row.id)
                                    setModalOpen(true);
                                    setQuestionData(row.original)
                                }}>

                                    <Visibility />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteQuestion(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}

                />
                <ViewQuestion
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)

                    }}
                    option={option}
                    questionData={questionData}
                />
            </div>
        </IonContent>

    );

};

const ViewQuestion: FC<{
    onClose: () => void;

    open: boolean;
    questionData: any;
    option: any

}> = (props) => {

    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">View Question</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        //gap: '1.5rem',
                    }}
                >
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Question</h2>
                            <p>{props.questionData.title}</p>
                        </IonLabel>
                    </IonItem>

                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Question Content</h2>
                            <p dangerouslySetInnerHTML={{ __html: props.questionData.q_content }}></p>
                        </IonLabel>
                    </IonItem>

                    <IonItem >
                        <IonLabel>
                            <h2 className="mb-3">Question options</h2>
                            {props.option.map((item: any, i: any) =>
                                <p className="mt-3" key={i}> {i + 1}. {item}</p>
                            )}
                        </IonLabel>
                    </IonItem>
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Answer</h2>
                            <p>{props.questionData.answer}</p>
                        </IonLabel>
                    </IonItem>
                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>

            </DialogActions>
        </Dialog>
    );
}

export default Question;
