import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonText,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import baseUrl, { api } from '../../pages/Urls';
import swal from 'sweetalert';
import { useForm, Controller, useFieldArray } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
const AddQuestion: React.FC<{

    level: any[]
    collapse: boolean
    onClose: () => void;
    course_name: string
    fetchShowCourse: () => void
}> = (props) => {
    const {
        handleSubmit,
        register,
        unregister,
        formState: { errors },
        reset,
        control,
        setValue,
        watch,
    } = useForm({
        defaultValues: {

            level_name: '',
            topic_name: '',
            title: '',
            q_content: '',
            question_type: '',
            answer: '',
            inputList: [{ option: '' }],


        }
    });
    const [topic_name, setTopic_name] = useState('');
    const [question, setQuestion] = useState('');
    const [q_content, setQContent] = useState('');
    const [questionType, setQuestionType] = useState('');
    const [answer, setAnswer] = useState('');
    const [lvlid, setLvlid] = useState('');
    const [topic, setTopic] = useState([]);

    const [inputList, setinputList] = useState([{ option: '' }]);
    const handleinputchange = (e: any, index: any) => {
        const { name, value } = e.target;
        const list: any[] = [...inputList];
        list[index][name] = value;
        setinputList(list);


    }
    const handleaddclick = () => {

        const abc = [...inputList, { option: '' }]

        setinputList(abc);
    }
    const handleremove = (index: any) => {
        const list = [...inputList];
        list.splice(index, 1);
        setinputList(list);
    }

    const handleSelectChange = (e: any) => {

        const lid = e.target.value;
        setLvlid(lid);
        if (lid) {
            fetch(`${baseUrl}${api.allLevelTopic}${lid}`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }
            })
                .then(resp => resp.json())
                .then(resp => {
                    setTopic(resp)
                })
        }

    }

    const addQuestion = (e: any) => {
        {
            let data = {

                'level_id': lvlid,
                'topic_name': topic_name,
                'title': question,
                'q_content': q_content,
                'question_type': questionType,
                'option': inputList,
                'answer': answer,

            }
            fetch(`${baseUrl}${api.addQuestion}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchShowCourse();
                        props.onClose();
                        setLvlid('');
                        setTopic_name('');
                        setQuestionType('');
                        setQuestion('');
                        setQContent('');
                        setAnswer('');
                        setinputList([{ option: '' }]);

                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }


    return (
        <Collapse in={props.collapse}>
            <div className="container mt-5 mb-5 ">

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">add Question</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>
                        <IonItem>
                            <IonLabel position="stacked">Level</IonLabel>
                            <IonSelect

                                value={lvlid}
                                onIonChange={(e: any) => handleSelectChange(e)}
                                {...register("level_name", { required: "Level Name Is Required" })}
                                placeholder="Select level" >
                                {props.level.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()} value={item.id}>{item.level_name}</IonSelectOption>
                                ))}
                            </IonSelect>

                        </IonItem>
                        <p style={{ color: "red" }}>
                            <ErrorMessage errors={errors} name='level_name' />
                        </p>
                        <IonItem>
                            <IonLabel position="stacked">Topic</IonLabel>
                            <IonSelect

                                value={topic_name}
                                onIonChange={(e: any) => setTopic_name(e.target.value)}
                                {...register("topic_name", { required: "Topic Name Is Required" })}
                                placeholder="Select topic" >
                                {topic.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()}>{item.topic_name}</IonSelectOption>
                                ))}
                            </IonSelect>

                        </IonItem>
                        <p style={{ color: "red" }}>
                            <ErrorMessage errors={errors} name='topic_name' />
                        </p>
                        <IonItem>
                            <IonLabel position="stacked">Question Type</IonLabel>

                            <IonSelect

                                {...register("question_type", { required: "Question type Is Required" })}
                                placeholder="Select question_type"
                                value={questionType}
                                onIonChange={(e: any) => setQuestionType(e.target.value)}
                            >

                                <>
                                    <IonSelectOption value='MCQ'>MCQ</IonSelectOption>
                                    <IonSelectOption value='SQ'>Short Question</IonSelectOption>

                                </>

                            </IonSelect>

                        </IonItem>
                        <p style={{ color: "red" }}>
                            <ErrorMessage errors={errors} name='question_type' />
                        </p>
                        <IonItem>
                            <IonLabel position="stacked">Question</IonLabel>
                            <IonInput type="text"
                                {...register("title", { required: "Question Title Is Required" })}
                                placeholder="question..."
                                onIonChange={(e: any) => setQuestion(e.target.value)}
                                clearInput></IonInput>
                        </IonItem>
                        <p style={{ color: "red" }}>
                            <ErrorMessage errors={errors} name='title' />
                        </p>
                        <IonItem>
                            <IonLabel position="stacked" >Content</IonLabel>
                            <div style={{ marginTop: "3%", marginBottom: "5%" }}>
                                <Editor
                                    textareaName="content"
                                    value={q_content}
                                    onEditorChange={(content: any, editor: any) => setQContent(content)}
                                    init={{
                                        height: 500,
                                        width: 1030,
                                        menubar: true,
                                        plugins: [

                                            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                        ],

                                        toolbar: 'undo redo | blocks | ' +
                                            'bold italic forecolor | alignleft aligncenter ' +
                                            'alignright alignjustify | bullist numlist outdent indent | ' +
                                            'removeformat | help',
                                        content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                    }}
                                />
                            </div>

                        </IonItem>

                        {questionType === 'MCQ' ?
                            <>
                                <IonItem>
                                    <IonText color="primary">
                                        <h2 className="mt-3 mb-3">Add Question Option</h2>
                                    </IonText>

                                </IonItem>
                                <IonItem>

                                    <div>

                                        {
                                            inputList.map((x, i) => {

                                                return (
                                                    <div key={i.toString()} className="row mb-3 mt-3">
                                                        <div className=" col-md-1">
                                                            <input className="mx-2 mt-4"
                                                                type="radio"

                                                                value={x.option}
                                                                {...register('answer', { required: "Select one Answer" })}
                                                                onChange={(e: any) => {
                                                                    setAnswer(e.target.value)

                                                                }}
                                                            />
                                                        </div>
                                                        <div className=" col-md-6">
                                                            <IonItem>
                                                                <IonLabel position="stacked">Option</IonLabel>
                                                                <IonInput type="text"

                                                                    value={x.option}
                                                                    {...register(`inputList.${i}.option`, { required: "Option Is Required" })}
                                                                    required
                                                                    clearInput
                                                                    onIonChange={(e: any) => handleinputchange(e, i)}
                                                                >
                                                                </IonInput>
                                                            </IonItem>
                                                            <p style={{ color: "red" }}>
                                                                <ErrorMessage errors={errors} name={`inputList.${i}.option`} />
                                                                <br></br>
                                                                <ErrorMessage errors={errors} name='answer' />
                                                            </p>
                                                        </div>
                                                        <div className="col-md-3 mt-4">
                                                            {
                                                                inputList.length !== 1 &&
                                                                <button onClick={() => handleremove(i)}>X</button>
                                                            }
                                                            {
                                                                inputList.length - 1 === i &&
                                                                <button onClick={() => handleaddclick()} className="mx-3 ">
                                                                    <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                                </button>
                                                            }

                                                        </div>



                                                    </div>
                                                );
                                            })}

                                    </div>

                                </IonItem>
                            </>
                            :
                            <IonItem>
                                <IonLabel position="stacked">Answer</IonLabel>
                                <IonInput type="text"
                                    {...register('answer', { required: "Answer Is Required" })}
                                    placeholder="ans..."
                                    value={answer}
                                    onIonChange={(e: any) => setAnswer(e.target.value)}
                                    clearInput></IonInput>
                            </IonItem>
                        }<p style={{ color: "red" }}>
                            <ErrorMessage errors={errors} name='answer' />
                        </p>
                        <hr />
                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="danger" onClick={() => {
                                props.onClose();

                            }}>
                                Cancel
                            </IonButton>
                            <IonButton color="primary" onClick={addQuestion}>
                                Create
                            </IonButton>

                        </div>
                    </IonCardContent>
                </IonCard>

            </div>

        </Collapse>
    )
}
export default AddQuestion;
