import {
    IonCardHeader,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonButton,
    IonCardContent,
    IonCard,
    IonItem,
    IonContent,
    IonCol, IonGrid, IonRow

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import baseUrl, { api } from '../../../pages/Urls';
import { format } from 'date-fns';
import "./ViewBlog.css";


const ViewBlog: React.FC = () => {
    type ViewBlogParams = {
        slug: string;
    };
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState(format(new Date(), 'yyyy-MM-dd'));
    const [category, setCategory] = useState([]);

    const history = useHistory();
    const { slug } = useParams<ViewBlogParams>();

    const viewArticle = () => {
        fetch(`${baseUrl}${api.viewArticle}${slug}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setTitle(result.title);
                setContent(result.content);
                setDate(result.date);
                setCategory(result.category);
            })
    }
    useEffect(() => {
        viewArticle();

    }, [])

    return (
        <IonContent>
            <IonGrid>
                <IonRow>
                    <IonCol>
                        <IonCard>

                            <IonCardHeader>
                                <IonToolbar>
                                    <div className="text-center">
                                        <IonTitle >
                                            {title}
                                        </IonTitle>
                                    </div>
                                    <IonButtons slot="start">
                                        <IonButton onClick={() => history.goBack()} > <i className="bi bi-backspace"> Back</i> </IonButton>
                                    </IonButtons>
                                </IonToolbar>
                            </IonCardHeader>


                            <IonCardContent>
                                <IonItem>
                                    <h1 color="dark">{title}</h1>

                                </IonItem>
                                <IonItem>
                                    <div
                                        id="article-content"
                                        dangerouslySetInnerHTML={{ __html: content }}
                                    />
                                </IonItem>
                                <IonItem>
                                    <IonGrid>
                                        <IonRow>
                                            <IonCol> {format(new Date(date), 'yyyy-MM-dd')}</IonCol>
                                            <IonCol>{category?.map((item: any, i: any) => {
                                                return <a key={i} className='text-decoration-none'>{(i ? ', ' : '') + item.name}</a>;
                                            })}</IonCol>

                                        </IonRow>
                                    </IonGrid>

                                </IonItem>
                            </IonCardContent>



                        </IonCard>
                    </IonCol>

                    <IonCol>

                    </IonCol>
                </IonRow>
            </IonGrid>


        </IonContent>

    );
};
export default ViewBlog;
