import {
    IonContent,
    IonItem,
    IonLabel,
    IonSegment,
    IonSegmentButton,
    IonRouterOutlet,
    IonCard,
    IonAccordion,
    IonAccordionGroup,
    IonText,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import { Redirect, Route, useLocation } from "react-router-dom";
import baseUrl, { api } from '../../pages/Urls';
import TchrTopic from './TchrTopic';
import TchrQuiz from './TchrQuiz';
import AddQuiz from './AddQuiz';
import TchrLevel from './TchrLevel';
import AddLevel from './AddLevel';
import TchrQuestion from './TchrQuestion';
import AddQuestion from './AddQuestion';


const ViewCourse: React.FC = () => {
    type ViewCourse = {
        id: string;
    };
    const history = useHistory()
    const location = useLocation()
    const { id } = useParams<ViewCourse>();
    const [course_name, setCourse_name] = useState('');
    const [courseId, setCourseId] = useState('');
    const [quiz, setQuiz] = useState([]);
    const [topic, setTopic] = useState([]);
    const [level, setLevel] = useState([]);
    const [question, setQuestion] = useState([]);
    const [topic_name, setTopic_name] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    const pathname = history.location.pathname;
    useEffect(() => {
        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setCourse_name(result.course.course_name)
                setCourseId(result.course.id)
                setQuiz(result.course.quiz)
                setTopic(result.course.topic)
                setLevel(result.course.level)
                setQuestion(result.course.question)
            })

    }, []);
    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }
    return (
        <IonContent>
            <IonText color="primary">
                <h1 className='mx-2'>{course_name}</h1>
            </IonText>

            <IonSegment onIonChange={segmentChangeHandler} scrollable value={pathname}>
                <IonSegmentButton value={`/dashboard/mycourse/${id}`}>Course</IonSegmentButton>
                <IonSegmentButton value={`/dashboard/mycourse/${id}/topic`}>Topic</IonSegmentButton>
                <IonSegmentButton value={`/dashboard/mycourse/${id}/quiz`}>Quiz</IonSegmentButton>
                <IonSegmentButton value={`/dashboard/mycourse/${id}/level`}>Level</IonSegmentButton>
                <IonSegmentButton value={`/dashboard/mycourse/${id}/question`}>Question</IonSegmentButton>
            </IonSegment>

            <IonContent className="ion-padding">

                <IonRouterOutlet >
                    <Route exact path="/dashboard/mycourse/:id">
                        <List quiz={quiz} topic={topic} />
                    </Route>
                    <Route exact path="/dashboard/mycourse/:id/topic">
                        <TchrTopic topic={topic} course_name={course_name} courseId={courseId} />
                    </Route >
                    <Route exact path="/dashboard/mycourse/:id/quiz">
                        <TchrQuiz quiz={quiz} courseId={courseId} />
                    </Route>
                    <Route exact path="/dashboard/mycourse/:id/quiz/add">
                        <AddQuiz level={level} course_name={course_name} courseId={courseId} />
                    </Route>
                    <Route exact path="/dashboard/mycourse/:id/level">
                        <TchrLevel level={level} courseId={courseId} />

                    </Route>
                    <Route exact path="/dashboard/mycourse/:id/level/add">
                        <AddLevel topic={topic} courseId={courseId} />
                    </Route>
                    <Route exact path="/dashboard/mycourse/:id/question">
                        <TchrQuestion courseId={courseId} question={question} />
                    </Route>
                    <Route exact path="/dashboard/mycourse/:id/question/add">
                        <AddQuestion level={level} courseId={courseId} />
                    </Route>
                    {/* <Route path="/dashboard" exact>
                        <Redirect to="/dashboard/mycourse/:id" />
                    </Route> */}
                </IonRouterOutlet>

            </IonContent>


        </IonContent >


    );


}

const List: React.FC<{
    quiz: any[],
    topic: any[]

}> = (props) => {

    return (

        <>

            <IonCard >
                <IonItem >
                    <IonAccordionGroup>
                        <IonAccordion value="first">
                            <IonItem slot="header" color="light">
                                <IonLabel>Quiz</IonLabel>
                            </IonItem>
                            <div className="ion-padding" slot="content">
                                {props.quiz.map((item: any, i: any) =>
                                    <p key={i.toString()}>{item.quiz_name}</p>
                                )}
                            </div>
                        </IonAccordion>
                    </IonAccordionGroup>
                </IonItem >
            </IonCard>

            {props.topic.map((item: any, i: any) =>
                <IonCard key={i.toString()}>
                    <IonItem >
                        <IonAccordionGroup>
                            <IonAccordion value="first">
                                <IonItem slot="header" color="light">
                                    <IonLabel>{item.topic_name}</IonLabel>
                                </IonItem>
                                <div className="ion-padding" slot="content">
                                    Topic Name: {item.id}
                                </div>
                            </IonAccordion>
                        </IonAccordionGroup>
                    </IonItem>
                </IonCard>
            )}

        </>


    );
};

export default ViewCourse;
