import {
    IonButton, IonButtons, IonHeader, IonTitle, IonToolbar, IonContent,
    IonIcon,
    IonItem,
    IonLabel,
    IonPopover,
    IonRouterLink,
} from '@ionic/react';
import {
    exitOutline,
    personCircle,
    settingsOutline,
} from "ionicons/icons";
import { Link } from 'react-router-dom';
import { useHistory } from "react-router";
import HeaderButton from './HeaderButton';
const Nav: React.FC<{
    isAuth: boolean;
}> = (props) => {
    const history = useHistory();
    const loginPage = () => {

        history.push("/login");
    };
    return (

        <IonHeader>
            <IonToolbar>
                <IonTitle>E-learning</IonTitle>
                {!props.isAuth ?
                    <IonButtons slot="secondary">
                        <IonButton><Link to="/home" >Home</Link></IonButton>
                        <IonButton onClick={loginPage}>Login</IonButton>
                    </IonButtons> :
                    <HeaderButton />}
            </IonToolbar>
        </IonHeader >

    );
};

export default Nav;
