import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonDatetime,
    IonPopover,
    IonContent,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import { format } from "date-fns";
const AddArticle: React.FC<{

    collapse: boolean
    group: any[]
    onClose: () => void;
    fetchArticle: () => void;

}> = (props) => {

    const [gname, setGname] = useState('');
    const [title, setTitle] = useState('');
    const [slug, setSlug] = useState('');
    const [content, setContent] = useState('');
    const [status, setStatus] = useState('');
    const [dateTimeValue, setDateTimeValue] = useState<any>(
        new Date().toISOString()
    );
    const addArticle = (e: any) => {
        {
            e.preventDefault();

            let data = {
                'title': title,
                'slug': slug,
                'content': content,
                'status': status,
                'gname': gname,
                'date': dateTimeValue,
            }

            fetch(`${baseUrl}${api.createArticle}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Article Successfully');
                        props.fetchArticle();
                        props.onClose();
                        setGname('');
                        setTitle('');
                        setSlug('');
                        setContent('');
                        setStatus('');
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }


    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">Add Article</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>
                        <IonItem>
                            <IonLabel position="stacked">Title</IonLabel>
                            <IonInput type="text"
                                name="title"
                                value={title}
                                required
                                clearInput
                                onIonChange={(e: any) => setTitle(e.target.value)}
                            >
                            </IonInput>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Slug</IonLabel>
                            <IonInput type="text"
                                name="slug"
                                value={slug}
                                required
                                clearInput
                                onIonChange={(e: any) => setSlug(e.target.value)}
                            >
                            </IonInput>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked" >Content</IonLabel>
                            <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                <Editor
                                    textareaName="content"
                                    value={content}
                                    onEditorChange={(content: any, editor: any) => setContent(content)}
                                    init={{
                                        height: 500,
                                        width: 1030,
                                        menubar: true,
                                        plugins: [

                                            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                        ],

                                        toolbar: 'undo redo | blocks | ' +
                                            'bold italic forecolor | alignleft aligncenter ' +
                                            'alignright alignjustify | bullist numlist outdent indent | ' +
                                            'removeformat | help',
                                        content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                    }}
                                />
                            </div>

                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Group</IonLabel>
                            <IonSelect
                                name="gname"
                                value={gname}
                                onIonChange={(e: any) => setGname(e.target.value)}

                                placeholder="Select groups" multiple={true}>
                                {props.group.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                ))}
                            </IonSelect>

                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Status</IonLabel>

                            <IonSelect
                                name="status"
                                onIonChange={(e: any) => setStatus(e.target.value)}
                            >
                                {(localStorage.getItem('role') == 'ar_mgmt') ?
                                    <>
                                        <IonSelectOption value='P'>Publish</IonSelectOption>
                                        <IonSelectOption value='D'>Draft</IonSelectOption>

                                    </>
                                    :
                                    <IonSelectOption value='U'>Review</IonSelectOption>

                                }
                            </IonSelect>

                        </IonItem>
                        <IonItem id="open-date-modal">
                            <IonLabel position="floating">Date </IonLabel>
                            <IonInput
                                type="text"
                                readonly
                                value={format(new Date(dateTimeValue), "yyyy-MM-dd")}
                            ></IonInput>
                        </IonItem>

                        <IonPopover trigger="open-date-modal">
                            <IonContent>
                                <IonDatetime
                                    onIonChange={(e) => setDateTimeValue(e.detail.value)}
                                    presentation="date"
                                    id="datetime"
                                    value={dateTimeValue}
                                />
                            </IonContent>
                        </IonPopover>

                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="danger" onClick={props.onClose}>
                                Cancel
                            </IonButton>
                            <IonButton color="primary" onClick={addArticle}>
                                Save
                            </IonButton>

                        </div>
                    </IonCardContent>
                </IonCard>

            </div>


        </Collapse>
    );
}

export default AddArticle;
