import {
    IonContent,
    IonCard, IonCardContent, IonCardHeader, IonCardTitle,
    IonCol, IonGrid, IonRow,
    IonItem, IonButton, IonNote,

} from '@ionic/react';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import TextField from '@mui/material/TextField';
import SearchIcon from '@mui/icons-material/Search';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../pages/Urls';
import { useHistory } from "react-router";
import { Link } from 'react-router-dom';
import { format } from 'date-fns'
import ReactPaginate from "react-paginate";
const Home: React.FC = () => {
    const [article, setArticle] = useState([]);
    const [allCategory, setAllCategory] = useState([]);
    const history = useHistory();
    const [pageNumber, setPageNumber] = useState(0);
    const articlePerPage = 10;
    const pagesVisited = pageNumber * articlePerPage;
    const pageCount = Math.ceil(article.length / articlePerPage);
    const changePage = ({ selected }: any) => {
        setPageNumber(selected);
    };
    const fetchblog = () => {
        fetch(`${baseUrl}${api.blog}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',

            },
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())

            })
    }

    const fetchCategory = () => {
        fetch(`${baseUrl}${api.allCategory}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',


            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setAllCategory(resp)
            })
    }
    useEffect(() => {
        fetchblog();
        fetchCategory();
    }, [])
    const search = (text: any) => {
        if (text) {
            let matches = article.filter((item: any) => {
                const regex = new RegExp(`${text}`, "gi");
                return item.title.match(regex);
            })
            setArticle(matches);
        } else {
            fetchblog();
        }


    };

    return (
        <IonContent>
            <IonGrid>
                <IonRow>
                    <IonCol>
                        <div className=" mt-3">
                            <h2>Recent Articles Post - {categoryName}</h2>

                            <div className="input-group rounded mt-3">
                                <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => search(e.target.value)} />

                            </div>
                            <Paper sx={{ width: '100%', mb: 2, mt: 2 }}>

                                <TableContainer>
                                    <Table aria-label="simple table">

                                        <TableBody>
                                            {(rowsPerPage > 0
                                                ? article.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                                : article
                                            ).map((item: any, i: any) => (
                                                <TableRow key={i.toString()}>
                                                    <TableCell >
                                                        <div>
                                                            <h4 className="text-primary" onClick={(e) => showModal(item.slug)}>{item.title}</h4>
                                                        </div>
                                                        <>

                                                            <IonModal isOpen={isOpen} class="fullscreen">

                                                                <IonHeader>
                                                                    <IonToolbar>
                                                                        <div className="text-center">
                                                                            <IonTitle >{title}
                                                                            </IonTitle>
                                                                        </div>
                                                                        <IonButtons slot="start">
                                                                            <IonButton onClick={() => setIsOpen(false)} > <i className="bi bi-backspace"> Back</i> </IonButton>
                                                                        </IonButtons>
                                                                    </IonToolbar>
                                                                </IonHeader>

                                                                <IonContent>
                                                                    <IonCardContent>
                                                                        <IonItem >
                                                                            <h1 color="dark">{title}</h1>

                                                                        </IonItem>
                                                                        <IonItem>
                                                                            <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>
                                                                        </IonItem>
                                                                        <IonItem>

                                                                            <IonGrid>
                                                                                <IonRow>
                                                                                    <IonCol>{format(new Date(date), 'yyyy-MM-dd')}</IonCol>
                                                                                    <IonCol>{category.map((item: any, i: any) => {
                                                                                        return <a key={i} className='text-decoration-none'>{(i ? ', ' : '') + item.name}</a>;
                                                                                    })}</IonCol>

                                                                                </IonRow>
                                                                            </IonGrid>


                                                                        </IonItem>
                                                                    </IonCardContent>

                                                                </IonContent>

                                                            </IonModal>

                                                        </>
                                                        <div>
                                                            {format(new Date(item.date), 'yyyy-MM-dd')}
                                                        </div>

                                                    </TableCell>


                                                </TableRow>
                                            ))}
                                        </TableBody>
                                    </Table>

                                </TableContainer>
                                <TablePagination
                                    rowsPerPageOptions={[10, 15, 20]}
                                    component="div"
                                    count={article.length}
                                    rowsPerPage={rowsPerPage}
                                    page={page}
                                    onPageChange={handleChangePage}
                                    onRowsPerPageChange={handleChangeRowsPerPage}
                                />
                            </Paper>
                        </div>
                    </IonCol>
                    <IonCol size="auto">
                        <div style={{ width: "200px" }}>
                            <h3>Categories</h3>

                            <IonItem >
                                <IonButton fill="clear" onClick={() => fetchblog()}>All Articles</IonButton>
                            </IonItem>
                            {allCategory.map((cat: any, i: any) =>
                                <IonItem key={i.toString()}>
                                    <IonButton fill="clear" onClick={() => {
                                        filterResult(cat.slug);
                                        setCateoryName(cat.name)
                                    }
                                    }>{cat.name}</IonButton>
                                </IonItem>
                            )}


                        </div>
                    </IonCol>
                </IonRow>
            </IonGrid>

        </IonContent>

    );
}
export default Home;
