import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
const ViewTopic: React.FC<{
    courseId: string

}> = (props) => {
    const [topic_name, setTopic_name] = useState('');
    const [id, setId] = useState('');
    const [content, setContent]: any[] = useState([]);
    const [article, setArticle]: any[] = useState([]);
    const handleAdd = () => {
        const abc = [...content, []]
        setContent(abc)
    }
    const handleChange = (onChangeValue: any, i: any) => {
        const inputdata = [...content]
        inputdata[i] = onChangeValue.target.value;
        setContent(inputdata)
    }
    const handleDelete = (i: any) => {
        const deletVal = [...content]
        deletVal.splice(i, 1)
        setContent(deletVal)
    }

    ///////////////////////////////////////
    const handleAddArt = () => {
        const abc = [...article, []]
        setArticle(abc)
    }

    const handleChangeArt = (onChangeValue: any, i: any) => {
        const inputdata = [...article]
        inputdata[i] = onChangeValue.target.value;
        setArticle(inputdata)
    }
    const handleDeleteArt = (i: any) => {
        const deletVal = [...article]
        deletVal.splice(i, 1)
        setArticle(deletVal)
    }
    return (
        <IonCard>
            <IonCardHeader>
                <IonCardTitle>
                    <IonToolbar>
                        <IonTitle><h1 className="text-center display-6">Topic</h1></IonTitle>
                    </IonToolbar>
                </IonCardTitle>
            </IonCardHeader>
            <hr />
            <IonCardContent>
                <IonItem>
                    <IonLabel position="stacked">Topic Name</IonLabel>
                    <IonInput type="text"
                        name="topic_name"
                        value={topic_name}
                        onIonChange={(e: any) => setTopic_name(e.target.value)}
                        clearInput

                    >
                    </IonInput>
                </IonItem>
                <IonText color="primary">
                    <h2 className="mt-3 text-center">Add Topic Content</h2>
                </IonText>


                <>
                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <button onClick={() => handleAdd()}>
                                    <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                </button>
                            </IonCardTitle>

                        </IonCardHeader>

                        <IonCardContent>
                            {content.map((op: any, i: any) => {
                                return (
                                    <div key={i.toString()} className="mt-2">




                                        <input type="file"
                                            name="content[]"
                                            placeholder="content..."
                                            value={op} onChange={e => handleChange(e, i)} />
                                        <button onClick={() => handleDelete(i)}>x</button>
                                    </div>
                                );
                            })}
                        </IonCardContent>


                    </IonCard>
                </>
                <IonText color="primary">
                    <h2 className="mt-3 text-center">Add Topic Article</h2>
                </IonText>
                <>
                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <button onClick={() => handleAddArt()}>
                                    <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                </button>
                            </IonCardTitle>

                        </IonCardHeader>

                        <IonCardContent>
                            {article.map((op: any, i: any) => {
                                return (
                                    <div key={i.toString()} className="mt-2">




                                        <input type="file"
                                            name="content[]"
                                            placeholder="content..."
                                            value={op} onChange={e => handleChangeArt(e, i)} />
                                        <button onClick={() => handleDeleteArt(i)}>x</button>
                                    </div>
                                );
                            })}
                        </IonCardContent>


                    </IonCard>
                </>
                <hr />
                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                    <IonButton color="primary" >
                        Save
                    </IonButton>

                </div>
            </IonCardContent>
        </IonCard>
    );
}
export default ViewTopic;
