import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewQuestion: React.FC = () => {
    type ViewQuestionParams = {
        id: string;
    };
    const [questions, setQuestions] = useState([]);
    const [total_question, setTotalQuestion] = useState('');
    const [mark, setMark] = useState('');
    const [level_id, setLevel_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [duration, setDuration] = useState('');
    const { id } = useParams<ViewQuestionParams>();
    const history = useHistory();


    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setQuestions(result);
                setTotalQuestion(result.length);
            })


            .catch(function (error) {
                console.log(error);
            })


    }, [])

    return (
        <>
            <IonContent>

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonGrid>
                                <IonRow>
                                    <IonCol>Total question: {total_question}</IonCol>
                                    <IonCol> Time: </IonCol>
                                </IonRow>
                            </IonGrid>
                        </IonCardTitle>
                    </IonCardHeader>

                    <IonCardContent>
                        <IonCard>

                            <IonCardContent>
                                {questions.map((item: any, i: any) => (
                                    <h2>{item.question}</h2>
                                ))}
                            </IonCardContent>

                        </IonCard>
                    </IonCardContent>




                </IonCard>

            </IonContent>

        </>

    );
}
export default ViewQuestion;
