import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../pages/Urls';
const AddQuiz: React.FC<{

    level: any[]
    course_name: string
    courseId: string

}> = (props) => {

    const [level_names, setLevel_names] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    const [status, setStatus] = useState('');
    const [error, setError] = useState("");
    const [date, setDate] = useState('');
    const [option, setOption]: any[] = useState([]);
    const handleAdd = () => {
        const abc = [...option, []]
        setOption(abc)
    }
    const handleChange = (onChangeValue: any, i: any) => {
        const inputdata = [...option]
        inputdata[i] = onChangeValue.target.value;
        setOption(inputdata)
    }
    const handleDelete = (i: any) => {
        const deletVal = [...option]
        deletVal.splice(i, 1)
        setOption(deletVal)
    }
    const addQuiz = (e: any) => {
        {
            let data = {
                'course_name': props.course_name,
                'quiz_name': quiz_name,
                'status': status,
                'level_names': level_names,
                'date': date,

            }
            fetch(`${baseUrl}${api.addQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Quiz Successfully');
                        window.location.href = `/dashboard/mycourse/${props.courseId}/quiz`;

                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <>
            <IonContent>
                <div className="container mt-5 mb-5 ">

                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <IonToolbar>
                                    <IonTitle><h1 className="text-center display-6">add quiz</h1></IonTitle>
                                </IonToolbar>
                            </IonCardTitle>
                        </IonCardHeader>
                        <hr />
                        <IonCardContent>

                            <IonItem>
                                <IonLabel position="stacked">Quiz Name</IonLabel>
                                <IonInput type="text"
                                    name="quiz_name"
                                    placeholder="quiz name..."
                                    onIonChange={(e: any) => setQuiz_name(e.target.value)}
                                    clearInput></IonInput>
                            </IonItem>
                            <IonItem>
                                <IonText color="primary">
                                    <h2 className="mt-3 text-center">Add Question Option</h2>
                                </IonText>
                            </IonItem>
                            <>
                                <IonCard>
                                    <IonCardHeader>
                                        <IonCardTitle>
                                            <button onClick={() => handleAdd()}>
                                                <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                            </button>
                                        </IonCardTitle>

                                    </IonCardHeader>

                                    <IonCardContent>
                                        {option.map((op: any, i: any) => {
                                            return (
                                                <div key={i.toString()} className="mt-2">
                                                    <input type="text"
                                                        name="option[]"
                                                        placeholder="options..."
                                                        value={op} onChange={e => handleChange(e, i)} />
                                                    <input className="mx-2"
                                                        type="radio"
                                                        name="answer"
                                                        value={op}
                                                        onChange={(e: any) => setLevel_names(e.target.value)}
                                                    />
                                                    <button onClick={() => handleDelete(i)}>x</button>
                                                </div>
                                            );
                                        })}
                                    </IonCardContent>


                                </IonCard>
                            </>

                            {/* <IonItem>
                                <IonLabel position="stacked">Level</IonLabel>
                                <IonSelect
                                    name="level_names"
                                    value={level_names}
                                    onIonChange={(e: any) => setLevel_names(e.target.value)}

                                    placeholder="Select levels" multiple={true}>
                                    {props.level.map((item: any, i: any) => (
                                        < IonSelectOption key={i.toString()}>{item.level_name}</IonSelectOption>
                                    ))}
                                </IonSelect>

                            </IonItem> */}
                            <IonItem>
                                <IonLabel position="stacked">Status</IonLabel>

                                <IonSelect
                                    name="status"
                                    value={status}
                                    onIonChange={(e: any) => setStatus(e.target.value)}
                                >

                                    <>
                                        <IonSelectOption>P</IonSelectOption>
                                        <IonSelectOption>D</IonSelectOption>

                                    </>

                                </IonSelect>

                            </IonItem>
                            <IonItem>
                                <IonLabel position="stacked">Date</IonLabel>

                                <IonInput type="date"
                                    name="date"
                                    value={date}
                                    required
                                    clearInput
                                    onIonChange={(e: any) => setDate(e.target.value)}
                                >
                                </IonInput>
                            </IonItem>

                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="primary" onClick={addQuiz}>
                                    Add Quiz
                                </IonButton>

                            </div>
                        </IonCardContent>
                    </IonCard>

                </div>
            </IonContent>
        </>
    )
}
export default AddQuiz;
