import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
    InputLabel,
    FormControl,
    Select
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Typography } from '@mui/material';
import { IonItem, IonLabel } from '@ionic/react';
type levelData = {
    id: string;
    level_name: string;
    topic_name: string;


};

//nested data is ok, see accessorKeys in ColumnDef below


const TchrLevel: FC<{
    topic: any,
    level: any,
    course_name: string,
    fetchShowCourse: () => void
}> = (props) => {

    const [levelData, setLevelData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);

    const columns = useMemo<MRT_ColumnDef<levelData>[]>(
        () => [

            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },
            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },


        ],
        [],
    );

    const deleteLevel = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteLevel}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        props.fetchShowCourse();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }
    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>
            <Collapse in={open}>
                <div id="collapseID">
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Article</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Title</IonLabel>
                                    <IonInput type="text"
                                        name="title"
                                        value={title}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setTitle(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Slug</IonLabel>
                                    <IonInput type="text"
                                        name="slug"
                                        value={slug}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setSlug(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked" >Content</IonLabel>
                                    <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                        <Editor
                                            textareaName="content"
                                            value={content}
                                            onEditorChange={(content: any, editor: any) => setContent(content)}
                                            init={{
                                                height: 500,
                                                width: 1030,
                                                menubar: true,
                                                plugins: [

                                                    'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                    'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                    'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                ],

                                                toolbar: 'undo redo | blocks | ' +
                                                    'bold italic forecolor | alignleft aligncenter ' +
                                                    'alignright alignjustify | bullist numlist outdent indent | ' +
                                                    'removeformat | help',
                                                content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                            }}
                                        />
                                    </div>

                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Group</IonLabel>
                                    <IonSelect
                                        name="gname"
                                        value={gname}
                                        onIonChange={(e: any) => setGname(e.target.value)}

                                        placeholder="Select groups" multiple={true}>
                                        {group.map((item: any, i: any) => (
                                            < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                        ))}
                                    </IonSelect>

                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Status</IonLabel>

                                    <IonSelect
                                        name="status"
                                        value={status}
                                        onIonChange={(e: any) => setStatus(e.target.value)}
                                    >
                                        {(localStorage.getItem('role') == 'ar_staff1') ?
                                            <IonSelectOption value='U'>Review</IonSelectOption> :
                                            <>
                                                <IonSelectOption value='P'>Publish</IonSelectOption>
                                                <IonSelectOption value='D'>Draft</IonSelectOption>

                                            </>



                                        }
                                    </IonSelect>

                                </IonItem>
                                <IonItem id="open-date-modal">
                                    <IonLabel position="floating">Date </IonLabel>
                                    <IonInput
                                        type="text"
                                        readonly
                                        value={format(new Date(dateTimeValue), "yyyy-MM-dd")}
                                    ></IonInput>
                                </IonItem>

                                <IonPopover trigger="open-date-modal">
                                    <IonContent>
                                        <IonDatetime
                                            onIonChange={(e) => setDateTimeValue(e.detail.value)}
                                            presentation="date"
                                            id="datetime"
                                            value={dateTimeValue}
                                        />
                                    </IonContent>
                                </IonPopover>

                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="danger" onClick={() => setOpen(false)}>
                                        Cancel
                                    </IonButton>
                                    <IonButton color="primary" onClick={updateArticle}>
                                        Save
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </div>
            </Collapse>
            <h1 className='text-center mb-2'>List of Levels</h1>
            <MaterialReactTable
                columns={columns}
                data={props.level}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                enableRowNumbers
                enableColumnOrdering
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="Edit">
                            <IconButton onClick={() => {
                                setLevelData(row.original)
                                setModalOpen(true);
                            }}>
                                <Edit />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteLevel(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add-Course">
                        <IconButton onClick={() => setModalOpen(true)}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}
            />

        </div>


    );

};

export default TchrLevel;
