import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewQuestion: React.FC = () => {
    type ViewQuestionParams = {
        id: string;
    };
    const [question, setQuestion] = useState('');
    const [total_question, setTotalQuestion] = useState('');
    const [mark, setMark] = useState('');
    const [level_id, setLevel_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [duration, setDuration] = useState('');

    const history = useHistory();
    const { id } = useParams<ViewQuestionParams>();

    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {

                setQuestion(result.question);


            })
            .catch(function (error) {
                console.log(error);
            })

    }, [])


    return (
        <>
            <IonContent>

                <IonCard>
                    <IonCardHeader>
                        <IonButton fill="clear" onClick={() => history.goBack()}> <i className="bi bi-arrow-left"></i></IonButton>
                        <IonCardTitle></IonCardTitle>
                    </IonCardHeader>

                    <IonCardContent>
                        <IonItem>
                            Per Question Mark:{question}
                        </IonItem>
                        <IonItem>
                            Total Time:
                        </IonItem>
                        <IonItem>
                            Total question:
                        </IonItem>
                    </IonCardContent>




                </IonCard>

            </IonContent>

        </>

    );
}
export default ViewQuestion;
