import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
    IonPopover,
    IonDatetime,

} from "@ionic/react";
import React, { FC, useMemo, useState } from 'react';
import MaterialReactTable, {
    MRT_ColumnDef,

} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Stack,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add, Visibility } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Collapse } from 'react-bootstrap'
import AddQuiz from './AddQuiz';
import { format } from 'date-fns';
import swal from 'sweetalert';
import Select from 'react-select';
import { useForm, Controller, useFieldArray } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { TextField } from "@mui/material";
import { DateTimePicker, LocalizationProvider } from "@mui/x-date-pickers";
type quizData = {
    id: string;
    start_date: Date;
    quiz_name: string;
    status: string;
    test_time: any;

};

const schema = yup.object().shape({
    quiz_name: yup.string()
        .required('Quiz Name is required'),
    test_time: yup.string()
        .required('Quiz duration is required'),
    status: yup.string()
        .required('Quiz Status is required'),
    start_date: yup.string()
        .required('Quiz Start date is required'),
    inputlist: yup.array().required().min(1),

})
const TchrQuiz: FC<{

    quiz: any
    level: any
    course_name: string
    fetchShowCourse: () => void
}> = (props) => {
    const [quizId, setQuizId] = useState('');
    const type = quizId ? "E" : "C";
    const {
        handleSubmit,
        register,
        formState: { errors },
        reset,
        control,
        setValue,
        clearErrors,
        getValues
    } = useForm({
        defaultValues: {
            id: '',
            course_name: props.course_name,
            quiz_name: '',
            test_time: '',
            status: '',
            inputlist: [{ level_name: '', qcount: '' }],
            start_date: format(new Date(), "yyyy-MM-dd HH:mm"),

        }
    });
    const { fields, remove, append } = useFieldArray({
        name: 'inputlist',
        control,
    });
    const [quiz_name, setQuiz_name] = useState('');

    const [status, setStatus] = useState('');
    const [dateTimeValue, setDateTimeValue] = useState<any>(
        format(new Date(), "yyyy-MM-dd HH:mm")
    );
    const [duration, setDuration] = useState('');
    const [level, setLevel] = useState([]);
    const [error, setError] = useState("");
    const [showQuiz, setShowQuiz] = useState([]);
    const [quizData, setQuizData] = useState({});
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const [modalOpen, setModalOpen] = useState(false);
    const [inputList, setinputList] = useState([{ level_name: '', qcount: '' }]);
    const handleinputchange = (e: any, index: any) => {
        const { name, value } = e.target;
        const list: any[] = [...inputList];
        list[index][name] = value;
        setinputList(list);


    }
    const handleaddclick = () => {

        const abc = [...inputList, { level_name: '', qcount: '' }]

        setinputList(abc);
    }
    const handleremove = (index: any) => {
        const list = [...inputList];
        list.splice(index, 1);
        setinputList(list);
    }


    const columns = useMemo<MRT_ColumnDef<quizData>[]>(
        () => [



            {
                accessorKey: 'quiz_name',
                header: 'Quiz Name',

            },
            {
                accessorKey: 'status',
                header: 'Status',
                Cell: ({ cell }) => (
                    cell.getValue<string>() === "P" ?
                        'Publish' : 'Draft'
                ),
            },
            {
                accessorKey: 'start_date',
                header: 'Start Date',

            },
            {
                accessorKey: 'test_time',
                header: 'Duration (Minutes)',

            },


        ],
        [],
    );



    const editQuiz = (id: any) => {
        setQuizId(id);
        fetch(`${baseUrl}${api.showQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setValue('quiz_name', result.quiz.quiz_name);
                setValue('status', result.quiz.status);
                setValue('start_date', result.quiz.start_date);
                setValue('test_time', result.quiz.test_time);
                setValue('id', result.quiz.id)
                setLevel(result.level);
                setShowQuiz(result.quiz.qlevel)
                let levelList: any[] = [];
                {
                    result.quiz.qlevel.map((item: any, i: any) =>
                        levelList.push({
                            level_name: item.level_name,
                            qcount: item.qcount

                        })
                    )
                }
                setValue('inputlist', [...levelList]);

            })
            .catch(function (error) {
                console.log(error);
            })


    }

    const addQuiz = (e: any) => {
        {

            fetch(`${baseUrl}${api.addQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchShowCourse();
                        setCollapse(false)

                        swal({
                            title: "Success!",
                            text: "Quiz Created successfully",
                            icon: "success",
                        });
                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    const updateQuiz = (e: any) => {

        fetch(`${baseUrl}${api.updateQuiz}${e.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(e),
        })

            .then(res => {
                if (res.status === 200) {
                    props.fetchShowCourse();
                    setOpen(false)
                    setinputList([]);
                    swal({
                        title: "Success!",
                        text: "Quiz updated successfully",
                        icon: "success",
                    });
                } else {
                    swal({
                        title: "Error!",
                        text: res.status + ' ' + res.statusText +
                            " - Quiz not updated",
                        icon: "error",
                    });
                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteQuiz = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuiz}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })
                .then(res => {
                    if (res.status === 200) {

                        props.fetchShowCourse();
                        swal({
                            title: "Success!",
                            text: "Quiz deleted successfully",
                            icon: "success",
                        });
                    } else {
                        swal({
                            title: "Error!",
                            text: res.status + ' ' + res.statusText +
                                " - Quiz not deleted",
                            icon: "error",
                        });
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    const onSubmit = (e: any) => {
        type === "C" ? addQuiz(e) : updateQuiz(e);
    }
    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>
            <Collapse in={collapse}>
                <div id="collapseID">
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">{type === "C" ? "Create" : "Edit"} Quiz</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <form onSubmit={handleSubmit(onSubmit)}>
                                    <IonItem>
                                        <IonLabel position="stacked">Quiz Name</IonLabel>
                                        <IonInput placeholder="quiz name"
                                            type="text"
                                            {...register("quiz_name", { required: "Quiz Name Is Required" })}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <p style={{ color: "red" }}>
                                        <ErrorMessage errors={errors} name="quiz_name" />
                                    </p>
                                    <IonItem>
                                        <IonText color="primary">
                                            <h2 className="mt-3 mb-3">Add Quiz Level</h2>
                                        </IonText>

                                    </IonItem>

                                    <div>

                                        {
                                            fields.map((x, i) => {

                                                return (
                                                    <div key={i.toString()} className="row mb-3 mt-3">
                                                        <div className=" col-md-4">
                                                            <IonItem>
                                                                <IonLabel position="stacked">Level</IonLabel>
                                                                <div className="mt-2" style={{ width: '100%' }}>

                                                                    <Controller
                                                                        control={control}
                                                                        name="crs_inst"

                                                                        render={() => (
                                                                            <Select

                                                                                menuPortalTarget={document.body}
                                                                                options={instructor}
                                                                                placeholder={<i className="bi bi-search"> Select Instructor</i>}
                                                                                value={getValues('crs_inst')}
                                                                                onChange={(e: any) => setValue('crs_inst', e)}
                                                                                isMulti

                                                                            />

                                                                        )}

                                                                    />
                                                                </div>
                                                                {/* <IonSelect

                                                                    {...register(`inputlist.${i}.level_name`, { required: "Level Name Is Required" })}
                                                                >
                                                                    {props.level.map((item: any, index: any) => (
                                                                        < IonSelectOption key={index.toString()}>{item.level_name}</IonSelectOption>
                                                                    ))}
                                                                </IonSelect> */}
                                                            </IonItem>
                                                            <p style={{ color: "red" }}>
                                                                <ErrorMessage errors={errors} name={`inputlist.${i}.level_name`} />
                                                            </p>
                                                        </div>
                                                        <div className=" col-md-4">
                                                            <IonItem>
                                                                <IonLabel position="stacked">No of Question</IonLabel>
                                                                <IonInput type="number"
                                                                    min={1}
                                                                    step='1'
                                                                    value={getValues(`inputlist.${i}.qcount`)}
                                                                    {...register(`inputlist.${i}.qcount`, { required: "Question No Is Required" })}
                                                                >
                                                                </IonInput>
                                                            </IonItem>
                                                            <p style={{ color: "red" }}>
                                                                <ErrorMessage errors={errors} name={`inputlist.${i}.qcount`} />
                                                            </p>
                                                        </div>
                                                        <div className="col-md-4 mt-4">
                                                            {
                                                                fields.length !== 1 &&
                                                                <button onClick={() => remove(i)}>X</button>
                                                            }
                                                            {
                                                                fields.length - 1 === i &&
                                                                <button className="mx-3 " onClick={() => append({ level_name: '', qcount: '' })}>
                                                                    <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                                </button>
                                                            }


                                                        </div>



                                                    </div>
                                                );
                                            })}

                                    </div>


                                    <IonItem>
                                        <IonLabel position="stacked">Status</IonLabel>

                                        <IonSelect
                                            placeholder="status"

                                            {...register("status", { required: "Status Is Required" })}
                                        >

                                            <>
                                                <IonSelectOption value='P'>Publish</IonSelectOption>
                                                <IonSelectOption value='D'>Draft</IonSelectOption>

                                            </>

                                        </IonSelect>

                                    </IonItem>
                                    <p style={{ color: "red" }}>
                                        <ErrorMessage errors={errors} name="status" />
                                    </p>
                                    <IonItem>
                                        <IonLabel position="stacked">Duration (Minutes)</IonLabel>

                                        <IonInput type="number"
                                            min={1}
                                            step='1'
                                            {...register("test_time")}

                                        >
                                        </IonInput>
                                    </IonItem>
                                    <p style={{ color: "red" }}>
                                        <ErrorMessage errors={errors} name="test_time" />
                                    </p>
                                    <IonItem >
                                        <IonLabel position="floating">Quiz Start </IonLabel>
                                        <Controller
                                            name="start_date"
                                            control={control}
                                            rules={{
                                                required: 'Date is required',
                                            }}
                                            defaultValue={format(new Date(), "yyyy-MM-dd HH:mm")}
                                            render={({ field: { ...rest } }) => (
                                                <LocalizationProvider dateAdapter={AdapterDateFns}>
                                                    <DateTimePicker

                                                        inputFormat="yyyy-MM-dd HH:mm"

                                                        value={rest.value}
                                                        onChange={(e: any) => {
                                                            setValue("start_date", format(e, "yyyy-MM-dd HH:mm"));

                                                        }}
                                                        renderInput={(params) => (
                                                            <TextField {...params} sx={{ mt: 3, width: '100%' }} />
                                                        )}
                                                    />
                                                </LocalizationProvider>
                                            )}
                                        />
                                    </IonItem>
                                    <p style={{ color: "red" }}>
                                        <ErrorMessage errors={errors} name="start_date" />
                                    </p>
                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="danger" onClick={() => setCollapse(false)}>
                                            Cancel
                                        </IonButton>
                                        <IonButton color="primary" type="submit">
                                            {type === "C" ? "Create" : "Save"}
                                        </IonButton>

                                    </div>
                                </form>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </div>
            </Collapse>

            <h1 className='text-center mb-2'>List of Quiz</h1>
            <MaterialReactTable
                columns={columns}
                data={props.quiz}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                enableRowNumbers
                enableColumnOrdering
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="Edit" >
                            <IconButton aria-expanded={collapse} aria-controls="collapseID" onClick={() => {
                                editQuiz(row.id)
                                setCollapse(!collapse)
                                clearErrors([
                                    'quiz_name',
                                    'test_time',
                                    'status',
                                    'inputlist',
                                    'start_date',

                                ])
                            }}>

                                <Edit />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="left" title="View" >
                            <IconButton onClick={() => {
                                editQuiz(row.id)
                                setModalOpen(true);
                                setQuizData(row.original)
                            }}>

                                <Visibility />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteQuiz(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add-Quiz">
                        <IconButton aria-expanded={collapse} onClick={() => {
                            setCollapse(!collapse)
                            setQuizId('')
                            reset({
                                course_name: props.course_name,
                                quiz_name: '',
                                test_time: '',
                                status: '',
                                inputlist: [{ level_name: '', qcount: '' }],
                                start_date: format(new Date(), "yyyy-MM-dd HH:mm"),
                            })
                        }}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}

            />
            <ViewQuiz
                open={modalOpen}
                onClose={() => {
                    setModalOpen(false)

                }}
                showQuiz={showQuiz}
                quizData={quizData}
            />
        </div>


    );

};


