    const [level_name, setLevel_name] = useState('');
    const [duration, setDuration] = useState('');
    const [qIdx, setQIdx] = useState(0);
    const { id } = useParams<ViewQuestionParams>();
    const history = useHistory();
    const next = () => {

        if (qIdx + 1 < questions.length) {
            setQIdx(qIdx + 1);
        } else {

        }
    };
    const previous = () => {

        if (qIdx + 1 == questions.length) {
            setQIdx(qIdx - 1);
        } else {

        }
    };

    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setQuestions(result);
                setQuestion(result.map((op: any) => (op.question)))
                setTotalQuestion(result.length);
                //setOptions(result.map((op: any) => (op.answer)));
            })


            .catch(function (error) {
                console.log(error);
            })


    }, [])
    //console.log('Q', question);
    return (
        <>
            <IonContent>

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonGrid>
                                <IonRow>
                                    <IonCol>Question: {qIdx + 1} / {total_question}</IonCol>
                                    <IonCol> Time: </IonCol>
                                </IonRow>
                            </IonGrid>
                        </IonCardTitle>
                    </IonCardHeader>

                    <IonCardContent>
                        <>
                            <IonCard>

                                <IonCardContent>
                                    {/* <h2>{questions.map((q: any) => (q[0].question))}</h2> */}
                                    {/* <ul>
                                        {questions[currentQuestion].answer.map((ans: any) => {
                                            return (
                                                <li
