import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonCol, IonGrid, IonRow
} from '@ionic/react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import './Style.css';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../pages/Urls';
import { format } from 'date-fns'
const Home: React.FC = () => {

    const [article, setArticle] = useState([]);
    const [allCategory, setAllCategory] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState(format(new Date(), 'yyyy-MM-dd'));
    const [category, setCategory] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [categoryName, setCateoryName] = useState('All Articles');
    const fetchblog = () => {
        fetch(`${baseUrl}${api.blog}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',

            },
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
                setCateoryName('All Articles')
            })
    }

    const fetchCategory = () => {
        fetch(`${baseUrl}${api.allCategory}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',


            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setAllCategory(resp)
            })
    }
    useEffect(() => {
        fetchblog();
        fetchCategory();
    }, [])
    const search = (text: any) => {
        let matches = article.filter((item: any) => {
            const regex = new RegExp(`${text}`, "gi");
            return item.course_name.match(regex);
        })
        setArticle(matches);
    };
    const showModal = (slug: any) => {
        setIsOpen(true);

        fetch(`${baseUrl}${api.viewArticle}${slug}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',

            },
        })
            .then(res => res.json())
            .then(result => {
                setTitle(result.title);
                setContent(result.content);
                setDate(result.date);
                setCategory(result.category);
            })
            .catch(function (error) {
                console.log(error);
            })

    };

    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - article.length) : 0;

    const filterResult = (slug: any) => {

        if (slug) {
            fetch(`${baseUrl}${api.catArticle}${slug}`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',


                }
            })
                .then(resp => resp.json())
                .then(resp => {
                    setArticle(resp)
                })
        }

        //setArticle(result);
    }
    return (
        <IonContent>
            <IonGrid>
                <IonRow>
                    <IonCol>
                        <div className=" mt-3">
                            <h2>Recent Articles Post - {categoryName}</h2>

                            <div className="input-group rounded mt-3">
                                <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => search(e.target.value)} />

                            </div>
                            <Paper sx={{ width: '100%', mb: 2, mt: 2 }}>

                                <TableContainer>
                                    <Table aria-label="simple table">

                                        <TableBody>
                                            {(rowsPerPage > 0
                                                ? article.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                                : article
                                            ).map((item: any, i: any) => (
                                                <TableRow key={i.toString()}>
                                                    <TableCell >
                                                        <div>
                                                            <h4 className="text-primary" onClick={(e) => showModal(item.slug)}>{item.title}</h4>
                                                        </div>
                                                        <>

                                                            <IonModal isOpen={isOpen} class="fullscreen">

                                                                <IonHeader>
                                                                    <IonToolbar>
                                                                        <div className="text-center">
                                                                            <IonTitle >{title}
                                                                            </IonTitle>
                                                                        </div>
                                                                        <IonButtons slot="start">
                                                                            <IonButton onClick={() => setIsOpen(false)} > <i className="bi bi-backspace"> Back</i> </IonButton>
                                                                        </IonButtons>
                                                                    </IonToolbar>
                                                                </IonHeader>

                                                                <IonContent>
                                                                    <IonCardContent>
                                                                        <IonItem >
                                                                            <h1 color="dark">{title}</h1>

                                                                        </IonItem>
                                                                        <IonItem>
                                                                            <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>
                                                                        </IonItem>
                                                                        <IonItem>

                                                                            <IonGrid>
                                                                                <IonRow>
                                                                                    <IonCol>{format(new Date(date), 'yyyy-MM-dd')}</IonCol>
                                                                                    <IonCol>{category.map((item: any, i: any) => {
                                                                                        return <a key={i} className='text-decoration-none'>{(i ? ', ' : '') + item.name}</a>;
                                                                                    })}</IonCol>

                                                                                </IonRow>
                                                                            </IonGrid>


                                                                        </IonItem>
                                                                    </IonCardContent>

                                                                </IonContent>

                                                            </IonModal>

                                                        </>
                                                        <div>
                                                            {format(new Date(item.date), 'yyyy-MM-dd')}
                                                        </div>

                                                    </TableCell>


                                                </TableRow>
                                            ))}
                                        </TableBody>
                                    </Table>

                                </TableContainer>
                                <TablePagination
                                    rowsPerPageOptions={[10, 15, 20]}
                                    component="div"
                                    count={article.length}
                                    rowsPerPage={rowsPerPage}
                                    page={page}
                                    onPageChange={handleChangePage}
                                    onRowsPerPageChange={handleChangeRowsPerPage}
                                />
                            </Paper>
                        </div>
                    </IonCol>
                    <IonCol size="auto">
                        <div style={{ width: "200px" }}>
                            <h3>Categories</h3>

                            <IonItem >
                                <IonButton fill="clear" onClick={() => fetchblog()}>All Articles</IonButton>
                            </IonItem>
                            {allCategory.map((cat: any, i: any) =>
                                <IonItem key={i.toString()}>
                                    <IonButton fill="clear" onClick={() => {
                                        filterResult(cat.slug);
                                        setCateoryName(cat.name)
                                    }
                                    }>{cat.name}</IonButton>
                                </IonItem>
                            )}


                        </div>
                    </IonCol>
                </IonRow>
            </IonGrid>

        </IonContent>

    );
}
export default Home;
