import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonCol, IonGrid, IonRow
} from '@ionic/react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import './Style.css';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../pages/Urls';
const Home: React.FC = () => {

    const [article, setArticle] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');
    const [category, setCategory] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [search, setSearch] = useState("");

    useEffect(() => {
        fetch(`${baseUrl}${api.blog}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',

            },
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }, [])
    const showModal = (slug: any) => {
        setIsOpen(true);

        fetch(`${baseUrl}${api.viewArticle}${slug}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',

            },
        })
            .then(res => res.json())
            .then(result => {
                setTitle(result.title);
                setContent(result.content);
                setDate(result.date);
                setCategory(result.category);
            })
            .catch(function (error) {
                console.log(error);
            })

    };

    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - article.length) : 0;


    return (
        <IonContent>
            <IonGrid>
                <IonRow>
                    <IonCol>1</IonCol>
                    <IonCol size="auto">
                        <div style={{ width: "200px" }}>2</div>
                    </IonCol>
                </IonRow>
            </IonGrid>
            <div className="container mt-3">
                <h2>Recent Articles Post</h2>

                <div className="input-group rounded mt-3">
                    <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => setSearch(e.target.value)} />

                </div>
                <Paper sx={{ width: '100%', mb: 2, mt: 2 }}>

                    <TableContainer>
                        <Table aria-label="simple table">

                            <TableBody>
                                {(rowsPerPage > 0
                                    ? article.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : article
                                ).filter((item: any) => {
                                    return search.toLowerCase() === '' ? item :
                                        item.title.toLowerCase().includes(search);

                                }).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell >
                                            <div>
                                                <h4 className="text-primary" onClick={(e) => showModal(item.slug)}>{item.title}</h4>
                                            </div>
                                            <>

                                                <IonModal isOpen={isOpen} class="fullscreen">

                                                    <IonHeader>
                                                        <IonToolbar>
                                                            <div className="text-center">
                                                                <IonTitle >{title}
                                                                </IonTitle>
                                                            </div>
                                                            <IonButtons slot="start">
                                                                <IonButton onClick={() => setIsOpen(false)} > <i className="bi bi-backspace"> Back</i> </IonButton>
                                                            </IonButtons>
                                                        </IonToolbar>
                                                    </IonHeader>

                                                    <IonContent>
                                                        <IonCardContent>
                                                            <IonItem >
                                                                <h1 color="dark">{title}</h1>

                                                            </IonItem>
                                                            <IonItem>
                                                                <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>
                                                            </IonItem>
                                                            <IonItem>

                                                                <IonGrid>
                                                                    <IonRow>
                                                                        <IonCol>{date}</IonCol>
                                                                        <IonCol>{category.map((item: any, i: any) => {
                                                                            return <a key={i} className='text-decoration-none'>{(i ? ', ' : '') + item.name}</a>;
                                                                        })}</IonCol>

                                                                    </IonRow>
                                                                </IonGrid>


                                                            </IonItem>
                                                        </IonCardContent>

                                                    </IonContent>

                                                </IonModal>

                                            </>
                                            <div>
                                                {item.date}
                                            </div>

                                        </TableCell>


                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[10, 15, 20]}
                        component="div"
                        count={article.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>
            </div>
        </IonContent>

    );
}
export default Home;
