import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
    IonPopover,
    IonDatetime,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import { Collapse } from 'react-bootstrap'
import baseUrl, { api } from '../../pages/Urls';
import { format } from "date-fns";
import swal from 'sweetalert';
import { useForm, Controller } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { TextField } from "@mui/material";
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
const AddQuiz: React.FC<{
    collapse: boolean
    onClose: () => void;
    level: any[]
    course_name: string
    fetchShowCourse: () => void

}> = (props) => {
    const {
        handleSubmit,
        register,
        formState: { errors },
        reset,
        control,
        setValue,
    } = useForm({
        defaultValues: {
            course_name: props.course_name,
            quiz_name: '',
            test_time: '',
            status: '',
            inputlist: [{ level_name: '', qcount: '' }],
            start_date: format(new Date(), "yyyy-MM-dd HH:mm"),

        }
    });
    const [quiz_name, setQuiz_name] = useState('');
    const [status, setStatus] = useState('');
    const [duration, setDuration] = useState('');
    const [error, setError] = useState("");
    const [dateTimeValue, setDateTimeValue] = useState<any>(
        format(new Date(), "yyyy-MM-dd HH:mm")
    );
    //console.log(dateTimeValue)
    /////
    const [inputList, setinputList] = useState([{ level_name: '', qcount: '' }]);
    const handleinputchange = (e: any, index: any) => {
        const { name, value } = e.target;
        const list: any[] = [...inputList];
        list[index][name] = value;
        setValue('inputlist', list);


    }
    const handleaddclick = () => {

        const abc = [...inputList, { level_name: '', qcount: '' }]

        setValue('inputlist', abc);
    }
    const handleremove = (index: any) => {
        const list = [...inputList];
        list.splice(index, 1);
        setValue('inputlist', list);
    }

    const addQuiz = (e: any) => {
        {

            fetch(`${baseUrl}${api.addQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchShowCourse();
                        props.onClose();
                        reset({
                            quiz_name: '',
                            test_time: '',
                            status: '',
                            inputlist: [{ level_name: '', qcount: '' }],
                            //start_date: format(new Date(), "yyyy-MM-dd HH:mm"),  
                        })
                        swal({
                            title: "Success!",
                            text: "Quiz Created successfully",
                            icon: "success",
                        });
                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <>
            <Collapse in={props.collapse}>

                <div className="container mt-5 mb-5 ">

                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <IonToolbar>
                                    <IonTitle><h1 className="text-center display-6">Add Quiz</h1></IonTitle>
                                </IonToolbar>
                            </IonCardTitle>
                        </IonCardHeader>
                        <hr />
                        <IonCardContent>
                            <IonItem>
                                <IonLabel position="stacked">Quiz Name</IonLabel>
                                <IonInput type="text"
                                    name="quiz_name"
                                    value={quiz_name}
                                    required
                                    clearInput
                                    onIonChange={(e: any) => setQuiz_name(e.target.value)}
                                >
                                </IonInput>
                            </IonItem>
                            <IonItem>
                                <IonText color="primary">
                                    <h2 className="mt-3 mb-3">Add Quiz Level</h2>
                                </IonText>

                            </IonItem>
                            <div>

                                {
                                    inputList.map((x, i) => {

                                        return (
                                            <div key={i.toString()} className="row mb-3 mt-3">
                                                <div className=" col-md-4">
                                                    <IonItem>
                                                        <IonLabel position="stacked">Level</IonLabel>
                                                        <IonSelect
                                                            name="level_name"
                                                            value={x.level_name}
                                                            onIonChange={(e: any) => handleinputchange(e, i)}
                                                        >
                                                            {props.level.map((item: any, index: any) => (
                                                                < IonSelectOption key={index.toString()}>{item.level_name}</IonSelectOption>
                                                            ))}
                                                        </IonSelect>
                                                    </IonItem>
                                                </div>
                                                <div className=" col-md-4">
                                                    <IonItem>
                                                        <IonLabel position="stacked">No of Question</IonLabel>
                                                        <IonInput type="number"
                                                            name="qcount"
                                                            value={x.qcount}
                                                            min={1}
                                                            step='1'
                                                            required
                                                            clearInput
                                                            onIonChange={(e: any) => handleinputchange(e, i)}
                                                        >
                                                        </IonInput>
                                                    </IonItem>
                                                </div>
                                                <div className="col-md-4 mt-4">
                                                    {
                                                        inputList.length !== 1 &&
                                                        <button onClick={() => handleremove(i)}>X</button>
                                                    }
                                                    {
                                                        inputList.length - 1 === i &&
                                                        <button onClick={() => handleaddclick()} className="mx-3 ">
                                                            <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                        </button>
                                                    }

                                                </div>



                                            </div>
                                        );
                                    })}

                            </div>


                            <IonItem>
                                <IonLabel position="stacked">Status</IonLabel>

                                <IonSelect
                                    name="status"
                                    value={status}
                                    onIonChange={(e: any) => setStatus(e.target.value)}
                                >

                                    <>
                                        <IonSelectOption value='P'>Publish</IonSelectOption>
                                        <IonSelectOption value='D'>Draft</IonSelectOption>

                                    </>

                                </IonSelect>

                            </IonItem>
                            <IonItem>
                                <IonLabel position="stacked">Duration (Minutes)</IonLabel>

                                <IonInput type="number"
                                    name="test_time"
                                    min={1}
                                    step='1'
                                    value={duration}
                                    required
                                    clearInput
                                    onIonChange={(e: any) => setDuration(e.target.value)}

                                >
                                </IonInput>
                            </IonItem>

                            <IonItem id="click-trigger">
                                <IonLabel position="floating">Quiz Start </IonLabel>
                                <IonInput
                                    type="text"
                                    clearInput
                                    value={format(new Date(dateTimeValue), "yyyy-MM-dd HH:mm")}
                                ></IonInput>
                            </IonItem>
                            <IonPopover trigger="click-trigger" triggerAction="click">
                                <IonContent>
                                    <IonDatetime onIonChange={(e) => setDateTimeValue(e.target.value)}></IonDatetime>
                                </IonContent>
                            </IonPopover>
                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="danger" onClick={() => {
                                    props.onClose();
                                    setinputList([]);
                                }}>
                                    Cancel
                                </IonButton>
                                <IonButton color="primary" onClick={addQuiz}>
                                    Create
                                </IonButton>

                            </div>
                        </IonCardContent>
                    </IonCard>

                </div>


            </Collapse>
        </>
    )
}
export default AddQuiz;
