import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    IconButton,
    Tooltip,
} from '@mui/material';
import { Approval, Input } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
type CourseData = {
    id: string;
    name: string;
    course_name: string;
    description: string;
    is_enrole: any;

};


const PendingEnrole: FC = () => {
    const [course, setCourse] = useState([]);
    const [courseData, setCourseData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);

    const fetchCourse = () => {
        fetch(`${baseUrl}${api.allPending}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCourse(resp.reverse())
            })
    }
    useEffect(() => {
        fetchCourse();

    }, [])

    const columns = useMemo<MRT_ColumnDef<CourseData>[]>(
        () => [


            {
                accessorKey: 'name',
                header: 'User Name',

            },
            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'description',
                header: 'Description',

            },
            {
                accessorKey: 'is_enrole',
                header: 'Status',
                Cell: ({ cell }) => (
                    cell.getValue<any>() === 0 && 'Pending'

                ),
            },

        ],
        [],
    );

    const approveEnrolement = (id: any) => {
        if (window.confirm('Want to Approve?')) {
            fetch(`${baseUrl}${api.approve}${id}`, {
                method: 'PUT',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },


            })
                .then(response => {
                    if (response.status === 200) {

                        fetchCourse();
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }

    }
    const disapproveEnrolement = (id: any) => {
        if (window.confirm('Want to Approve?')) {
            fetch(`${baseUrl}${api.approve}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },


            })
                .then(response => {
                    if (response.status === 200) {

                        fetchCourse();
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }

    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>Pending Enrole Courses List</h1>
                <MaterialReactTable
                    columns={columns}
                    data={course}
                    getRowId={(row => `${row.id}`)}
                    enableRowNumbers
                    enableRowActions
                    positionActionsColumn="last"
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>

                            <Tooltip arrow placement="right" title="Approved">
                                <IconButton style={{ color: "green" }} onClick={() => approveEnrolement(row.id)}>
                                    <Approval />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="disapproved">
                                <IconButton style={{ color: "red" }} onClick={() => disapproveEnrolement(row.id)}>
                                    <Approval />
                                </IconButton>
                            </Tooltip>

                        </Box>
                    )}

                />

            </div>
        </IonContent>

    );

};

export default PendingEnrole;
