import {
    IonContent, IonCard, IonCardContent, IonCardHeader, IonCardTitle, IonCardSubtitle,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import baseUrl, { api } from '../../pages/Urls';
import { Link } from 'react-router-dom';


const MyCourse: React.FC = () => {

    const [mycourse, setMyCourse] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [search, setSearch] = useState('');
    const fetchMycourse = () => {
        fetch(`${baseUrl}${api.myCourse}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setMyCourse(resp.reverse())
            })
    }
    useEffect(() => {
        fetchMycourse();
    }, []);


    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - mycourse.length) : 0;



    return (
        <IonContent>
            <div className="container mt-5">

                <Paper sx={{ width: '100%', mb: 2 }}>
                    <p className="h1 text-center">My Courses</p>
                    <div className="input-group rounded">
                        <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => setSearch(e.target.value)} />

                    </div>
                    <TableContainer>
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>

                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {(rowsPerPage > 0
                                    ? mycourse.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : mycourse
                                ).filter((item: any) => {
                                    return search.toLowerCase() === '' ? item :
                                        item.course_name.toLowerCase().includes(search);

                                }).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell >
                                            <IonCard>
                                                <IonCardHeader>
                                                    <IonCardTitle>
                                                        <Link className='text-decoration-none' to={`/dashboard/mycourse/${item.course_id}`}> {item.course_name}</Link>
                                                    </IonCardTitle>
                                                    <div className="text-end">
                                                        <IonCardSubtitle>
                                                            <button>g</button>
                                                        </IonCardSubtitle>
                                                    </div>

                                                </IonCardHeader>

                                                <IonCardContent>
                                                    {item.description}
                                                </IonCardContent>

                                            </IonCard>

                                        </TableCell>

                                    </TableRow>
                                ))}


                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[5, 10, 20]}
                        component="div"
                        count={mycourse.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>

            </div>
        </IonContent>
    );

}

export default MyCourse;
