import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
import { Typography } from '@mui/material';
type questionData = {
    id: string;
    course_name: string;
    topic_name: string;
    level_name: string;
    title: string;
    question_type: string;
    answer: string;



};

//nested data is ok, see accessorKeys in ColumnDef below


const Question: FC = () => {
    const [questions, setQuestions] = useState([]);
    const [questionData, setQuestionData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const fetchQuestions = () => {
        fetch(`${baseUrl}${api.allQuestion}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setQuestions(resp.reverse())
            })
    }
    useEffect(() => {
        fetchQuestions();
    }, [])

    const columns = useMemo<MRT_ColumnDef<questionData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
            },

            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },
            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },
            {
                accessorKey: 'title',
                header: 'Question',

            },

            {
                accessorKey: 'question_type',
                header: 'Question Type',

            },

            {
                accessorKey: 'answer',
                header: 'Question Answer',

            },

        ],
        [],
    );

    const deleteQuestion = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuestion}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchQuestions();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    //console.log(questionData)
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Questions</h1>
                <EditQuestion

                    fetchlevel={() => fetchQuestions()}
                    levelData={questionData}
                />
                <MaterialReactTable
                    columns={columns}
                    data={questions}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit" >
                                <IconButton data-bs-toggle="collapse" data-bs-target="#demo" onClick={() => {
                                    setQuestionData(row.original)

                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteQuestion(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}

                />

            </div>
        </IonContent>

    );

};

const EditQuestion: FC<{
    fetchlevel: () => void;
    levelData: any

}> = (props) => {
    const [question, setQuestion] = useState('');
    const [q_content, setQContent] = useState('');
    const [questionType, setQuestionType] = useState('');
    const [answer, setAnswer] = useState('');
    const [option, setOption]: any[] = useState([]);
    const [level_name, setLevel_name] = useState('');
    const [error, setError] = useState("");
    useEffect(() => {
        setLevel_name(props.levelData.level_name);
    }, [props.levelData])


    return (
        <div id="demo" className="collapse mt-3 mb-3">
            <p>dsfgdg</p>
        </div>
    );
}

export default Question;
