} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import { Redirect, Route, useLocation } from "react-router-dom";
import Course from '../admin/quiz/Course';
import baseUrl, { api } from '../../pages/Urls';
import { IonReactRouter } from '@ionic/react-router';
const ViewCourse: React.FC = () => {
    type ViewCourse = {
        id: string;
    };
    const history = useHistory()
    const location = useLocation()
    const { id } = useParams<ViewCourse>();
    const [course_name, setCourse_name] = useState('');
    const [quiz, setQuiz] = useState([]);
    const [topic, setTopic] = useState([]);
    const [topic_name, setTopic_name] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    const pathname = history.location.pathname;
    useEffect(() => {
        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setCourse_name(result.course.course_name)
                setQuiz(result.course.quiz)
                setTopic(result.course.topic)
            })

    }, []);
    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }
    return (
        <IonCard>
            <IonCardHeader>
                <IonCardTitle>{course_name}</IonCardTitle>
                <IonCardSubtitle>
                    <IonSegment onIonChange={segmentChangeHandler} scrollable value={pathname}>
                        <IonSegmentButton value={`/dashboard/mycourse/${id}`}>Course</IonSegmentButton>
                        <IonSegmentButton value={`/dashboard/mycourse/${id}/topic`}>Topic</IonSegmentButton>
                        <IonSegmentButton value={`/dashboard/mycourse/${id}/quiz`}>Quiz</IonSegmentButton>
                        <IonSegmentButton value={`/dashboard/mycourse/${id}/level`}>Level</IonSegmentButton>
                        <IonSegmentButton value={`/dashboard/mycourse/${id}/question`}>Question</IonSegmentButton>
                    </IonSegment>
                </IonCardSubtitle>
            </IonCardHeader>

            <IonCardContent>

                <MyCourseRoute />
            </IonCardContent>


        </IonCard>


    );


}
const MyCourseRoute: React.FC = () => {
    return (
        <IonRouterOutlet>
            <Route path="/dashboard/mycourse/:id" exact={true}>
                <Listt />
            </Route>
            <Route path="/dashboard/mycourse/:id/topic" exact component={Course} />

        </IonRouterOutlet>
    );
};
