import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
    IonList,
    IonRadioGroup,
    IonRadio,
    IonPopover,
    IonDatetime,

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add, Visibility } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import AddArticle from "./AddArticle";
import { format } from "date-fns";
type articleData = {
    id: string;
    user: {
        name: string;
    };
    title: string;
    slug: string;
    date: string;
    status: string;


};

//nested data is ok, see accessorKeys in ColumnDef below


const Articles: FC = () => {
    const [article, setArticle] = useState([]);
    const [group, setGroup] = useState([])
    const [title, setTitle] = useState('');
    const [slug, setSlug] = useState('');
    const [content, setContent] = useState('');
    const [status, setStatus] = useState('');
    const [id, setId] = useState('');
    const [gname, setGname] = useState([]);
    const [error, setError] = useState("");
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const [modalOpen, setModalOpen] = useState(false);
    const [articleData, setArticleData] = useState({});
    const [dateTimeValue, setDateTimeValue] = useState<any>(
        format(new Date(), "yyyy-MM-dd")
    );
    const fetchArticle = () => {
        fetch(`${baseUrl}${api.allArticle}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }
    const fetchGroup = () => {
        fetch(`${baseUrl}${api.allGroup}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setGroup(resp)
            })
    }
    useEffect(() => {
        fetchArticle();
        fetchGroup();
    }, [])

    const columns = useMemo<MRT_ColumnDef<articleData>[]>(
        () => [


            {
                accessorKey: 'user.name',
                header: 'Created By',

            },
            {
                accessorKey: 'title',
                header: 'Title',

            },
            {
                accessorKey: 'slug',
                header: 'Slug',

            },
            {
                accessorKey: 'status',
                header: 'Status',
                Cell: ({ cell }) => (
                    cell.getValue<string>() === "P" && 'Publish' ||
                    cell.getValue<string>() === "D" && 'Draft' ||
                    cell.getValue<string>() === "U" && 'Pending'

                ),

            },
            {
                accessorKey: 'date',
                header: 'Date',

            },


        ],
        [],
    );



    const editArticle = (id: any) => {

        fetch(`${baseUrl}${api.editArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setTitle(result.article.title);
                setSlug(result.article.slug);
                setContent(result.article.content);
                setStatus(result.article.status);
                setDateTimeValue(result.article.date);
                setId(result.article.id);

                setGname(

                    result.article.group.map((grp: any) => (grp.name))

                );
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateArticle = () => {
        let data = {
            'title': title,
            'slug': slug,
            'content': content,
            'status': status,
            'date': dateTimeValue,
            'gname': gname,
        }
        fetch(`${baseUrl}${api.updatetArticle}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    fetchArticle();
                    setOpen(false)

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteArticle = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteArticle}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchArticle();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    //console.log(questionData)
    return (
        <IonContent>
            <div className="container mt-5">

                <Collapse in={open}>
                    <div id="collapseID">
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Article</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Title</IonLabel>
                                        <IonInput type="text"
                                            name="title"
                                            value={title}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setTitle(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Slug</IonLabel>
                                        <IonInput type="text"
                                            name="slug"
                                            value={slug}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setSlug(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked" >Content</IonLabel>
                                        <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                            <Editor
                                                textareaName="content"
                                                value={content}
                                                onEditorChange={(content: any, editor: any) => setContent(content)}
                                                init={{
                                                    height: 500,
                                                    width: 1030,
                                                    menubar: true,
                                                    plugins: [

                                                        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                    ],

                                                    toolbar: 'undo redo | blocks | ' +
                                                        'bold italic forecolor | alignleft aligncenter ' +
                                                        'alignright alignjustify | bullist numlist outdent indent | ' +
                                                        'removeformat | help',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                                }}
                                            />
                                        </div>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Group</IonLabel>
                                        <IonSelect
                                            name="gname"
                                            value={gname}
                                            onIonChange={(e: any) => setGname(e.target.value)}

                                            placeholder="Select groups" multiple={true}>
                                            {group.map((item: any, i: any) => (
                                                < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                            ))}
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Status</IonLabel>

                                        <IonSelect
                                            name="status"
                                            value={status}
                                            onIonChange={(e: any) => setStatus(e.target.value)}
                                        >
                                            {(localStorage.getItem('role') == 'ar_mgmt') ?
                                                <>
                                                    <IonSelectOption>P</IonSelectOption>
                                                    <IonSelectOption>D</IonSelectOption>
                                                    <IonSelectOption>U</IonSelectOption>
                                                </>
                                                :
                                                <IonSelectOption>U</IonSelectOption>

                                            }
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem id="open-date-modal">
                                        <IonLabel position="floating">Date </IonLabel>
                                        <IonInput
                                            type="text"
                                            readonly
                                            value={format(new Date(dateTimeValue), "yyyy-MM-dd")}
                                        ></IonInput>
                                    </IonItem>

                                    <IonPopover trigger="open-date-modal">
                                        <IonContent>
                                            <IonDatetime
                                                onIonChange={(e) => setDateTimeValue(e.detail.value)}
                                                presentation="date"
                                                id="datetime"
                                                value={dateTimeValue}
                                            />
                                        </IonContent>
                                    </IonPopover>

                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="danger" onClick={() => setOpen(false)}>
                                            Cancel
                                        </IonButton>
                                        <IonButton color="primary" onClick={updateArticle}>
                                            Save
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </div>
                </Collapse>
                <AddArticle collapse={collapse} group={group} onClose={() => setCollapse(false)} fetchArticle={() => fetchArticle()} />
                <h1 className='text-center mb-2'>List of Articles</h1>
                <MaterialReactTable
                    columns={columns}
                    data={article}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableRowNumbers
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit" >
                                <IconButton aria-expanded={open} aria-controls="collapseID" onClick={() => {
                                    editArticle(row.id)
                                    setOpen(!open)

                                }}>

                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="left" title="View" >
                                <IconButton onClick={() => {
                                    editArticle(row.id)
                                    setModalOpen(true);
                                    setArticleData(row.original)
                                }}>

                                    <Visibility />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Trash">
                                <IconButton style={{ color: "green" }} onClick={() => deleteArticle(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-Article">
                            <IconButton aria-expanded={collapse} onClick={() => setCollapse(!collapse)}>
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}

                />

            </div>
        </IonContent>

    );

};


export default Articles;
